// ALGORITHM: Determine Next Date to Process
// Handle different input formats from n8n Notion node
let entries = [];

// Try different ways to access the data
if (Array.isArray($json)) {
  entries = $json;
} else if ($json.results && Array.isArray($json.results)) {
  entries = $json.results;
} else if ($input && $input.all) {
  entries = $input.all().map(item => item.json);
} else {
  entries = [$json].filter(item => item && typeof item === 'object');
}

console.log(`üìä Found ${entries.length} backlog entries`);
if (entries.length > 0) {
  console.log(`üìã First entry structure:`, JSON.stringify(entries[0], null, 2));
}

// Helper function: get current Eastern time date
function getEasternDate() {
  const now = new Date();
  // Eastern time is UTC-5 (standard) or UTC-4 (daylight), use UTC-4 for July
  const easternOffset = -4 * 60; // minutes
  const utc = now.getTime() + (now.getTimezoneOffset() * 60000);
  const easternTime = new Date(utc + (easternOffset * 60000));
  return easternTime;
}

// Helper function: format date to YYYY-MM-DD
function formatDate(date) {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
}

// Helper function: get yesterday's date in Eastern time
function getYesterday() {
  const easternNow = getEasternDate();
  const yesterday = new Date(easternNow);
  yesterday.setDate(yesterday.getDate() - 1);
  return formatDate(yesterday);
}

// Helper function: add days to date
function addDays(dateStr, days) {
  if (!dateStr || typeof dateStr !== 'string') {
    console.log(`‚ö†Ô∏è Invalid date string: ${dateStr}`);
    return null;
  }
  const date = new Date(dateStr + 'T12:00:00'); // Use noon to avoid timezone issues
  if (isNaN(date.getTime())) {
    console.log(`‚ö†Ô∏è Cannot parse date: ${dateStr}`);
    return null;
  }
  date.setDate(date.getDate() + days);
  return formatDate(date);
}

// Helper function: check if entry has valid data
function isValidEntry(entry) {
  if (!entry || typeof entry !== 'object') return false;

  // Check if it has any meaningful properties (specifically Gmail Date or Status)
  const hasGmailDate = entry.property_gmail_date || 
                      entry.properties?.["Gmail Date"] || 
                      entry.name;
  const hasStatus = entry.property_status || 
                   entry.properties?.Status;

  return !!(hasGmailDate || hasStatus || entry.id);
}

// Filter out empty objects and invalid entries
const validEntries = entries.filter(isValidEntry);

console.log(`üìä Valid entries after filtering: ${validEntries.length}`);

// Log current Eastern time for debugging
const easternNow = getEasternDate();
const today = formatDate(easternNow);
const yesterday = getYesterday();
console.log(`üìÖ Current Eastern time: ${easternNow.toISOString()}`);
console.log(`üìÖ Today (Eastern): ${today}, Yesterday (Eastern): ${yesterday}`);

// STEP 1: If no valid entries exist, return yesterday
if (validEntries.length === 0) {
  console.log(`‚úÖ No valid entries found, creating entry for yesterday: ${yesterday}`);
  return [{
    json: {
      action: 'create',
      nextDate: yesterday,
      reason: 'No entries exist - starting with yesterday'
    }
  }];
}

// STEP 2: Check ALL entries for incomplete status (CRITICAL FIX)
// If ANY entry is not complete, block creation
const incompleteEntries = validEntries.filter(entry => {
  const status = entry.property_status || 
                entry.properties?.Status?.select?.name;
  const date = entry.property_gmail_date || 
              entry.properties?.["Gmail Date"]?.title?.[0]?.text?.content ||
              entry.name;

  console.log(`üìã Entry ${date}: status = ${status}`);
  return status && status !== 'complete';
});

if (incompleteEntries.length > 0) {
  const incompleteInfo = incompleteEntries.map(entry => {
    const status = entry.property_status || 
                  entry.properties?.Status?.select?.name;
    const date = entry.property_gmail_date || 
                entry.properties?.["Gmail Date"]?.title?.[0]?.text?.content ||
                entry.name;
    return `${date}(${status})`;
  }).join(', ');

  console.log(`‚ö†Ô∏è  Found ${incompleteEntries.length} incomplete entries: ${incompleteInfo}`);
  return [{
    json: {
      action: 'warning',
      message: `Cannot create new entries - ${incompleteEntries.length} entries are not complete: ${incompleteInfo}`,
      incompleteCount: incompleteEntries.length,
      incompleteEntries: incompleteInfo
    }
  }];
}

// STEP 3: All entries are complete - proceed with date logic
console.log(`‚úÖ All entries are complete - proceeding with date logic`);

// Get latest entry for date calculation
const latestEntry = validEntries[0];
const latestDate = latestEntry.property_gmail_date || 
                  latestEntry.properties?.["Gmail Date"]?.title?.[0]?.text?.content ||
                  latestEntry.name;

console.log(`üìã Latest complete entry: ${latestDate}`);

// STEP 4: Find earliest missing date (gap finding)
// Get all dates and sort them
const allDates = validEntries
  .map(entry => {
    return entry.property_gmail_date || 
           entry.properties?.["Gmail Date"]?.title?.[0]?.text?.content ||
           entry.name;
  })
  .filter(date => date && typeof date === 'string' && date.match(/^\d{4}-\d{2}-\d{2}$/))
  .sort();

console.log(`üìÖ All valid dates: ${allDates.join(', ')}`);

// Find the first gap
for (let i = 0; i < allDates.length - 1; i++) {
  const currentDate = allDates[i];
  const nextDate = allDates[i + 1];

  const expectedNext = addDays(currentDate, 1);
  if (!expectedNext) continue;

  if (expectedNext !== nextDate) {
    console.log(`üîç Found gap: ${currentDate} -> ${nextDate}, missing: ${expectedNext}`);
    return [{
      json: {
        action: 'create',
        nextDate: expectedNext,
        reason: `Found gap between ${currentDate} and ${nextDate}`
      }
    }];
  }
}

// STEP 5: No gaps found, create entry for day before oldest (with existence check)
const oldestDate = allDates[allDates.length - 1]; // Since sorted ascending, last is oldest
let candidate = addDays(oldestDate, -1);
if (!candidate) {
  console.log(`‚ùå Cannot calculate day before oldest: ${oldestDate}`);
  return [{
    json: {
      action: 'create',
      nextDate: yesterday,
      reason: 'Date calculation error - using yesterday'
    }
  }];
}

// Keep going back until we find a date that doesn't exist
while (candidate && allDates.includes(candidate) && candidate >= '2020-01-01') {
  console.log(`üìã Date ${candidate} already exists, trying day before`);
  candidate = addDays(candidate, -1);
}

// Never create today or future dates
const nextDate = candidate > yesterday ? yesterday : candidate;

console.log(`üìÜ No gaps found. Oldest: ${oldestDate}, candidate: ${candidate}, yesterday: ${yesterday}`);
console.log(`‚úÖ Creating entry for: ${nextDate}`);

return [{
  json: {
    action: 'create',
    nextDate: nextDate,
    reason: `No gaps found, creating entry for earliest missing date before ${oldestDate}`
  }
}];

// Extract backlog details and build Gmail query
const backlogEntry = $json;
const algorithmResult = $('N03: Determine Next Date Algorithm').first().json;

// Get the date from algorithm result
const dateStr = algorithmResult.nextDate; // Format: YYYY-MM-DD

// Parse date components
const [year, month, day] = dateStr.split('-');
const formattedDate = `${year}/${month}/${day}`;

// Calculate next day for 'before' parameter
const dateObj = new Date(year, month - 1, day); // month is 0-indexed
const nextDay = new Date(dateObj);
nextDay.setDate(dateObj.getDate() + 1);

const afterDate = formattedDate;
const beforeDate = `${nextDay.getFullYear()}/${String(nextDay.getMonth() + 1).padStart(2, '0')}/${String(nextDay.getDate()).padStart(2, '0')}`;

// Build Gmail query with folder filter
const gmailQuery = `after:${afterDate} before:${beforeDate} in:"--Watch/VC"`;

console.log(`üìÖ Backlog entry created for: ${dateStr}`);
console.log(`üìß Gmail query: ${gmailQuery}`);

return [{
  json: {
    backlogId: backlogEntry.id,
    backlogDate: dateStr,
    backlogUrl: backlogEntry.url,
    gmailQuery: gmailQuery,
    formattedDate: formattedDate
  }
}];

// Process Gmail results and handle errors/empty results
const gmailMessages = $('N07: Search Gmail Messages').all();
const backlogDetails = $('N06: Extract Backlog Details & Build Query').first().json;

// Handle Gmail API errors
if (gmailMessages.length === 1 && gmailMessages[0].json.error) {
  const error = gmailMessages[0].json.error;
  console.error('‚ùå Gmail API error:', error);

  // Return empty array to trigger no emails path
  return [];
}

// Check for empty results - FIXED to handle empty JSON objects
if (!gmailMessages || gmailMessages.length === 0 || 
    (gmailMessages.length === 1 && !gmailMessages[0].json.id) ||
    (gmailMessages.length === 1 && Object.keys(gmailMessages[0].json).length === 0)) {
  console.log('‚ÑπÔ∏è No messages found for the specified date');
  // Return empty array to trigger no emails path
  return [];
}

// Process and format messages
console.log(`üìß Processing ${gmailMessages.length} Gmail messages`);

// Add backlogId to each message for subworkflow
const processedMessages = gmailMessages.map(item => {
  const msg = item.json;
  return {
    messageId: msg.id,
    threadId: msg.threadId,
    from: msg.From || 'Unknown',
    subject: msg.Subject || 'No Subject',
    backlogId: backlogDetails.backlogId
  };
});

return processedMessages.map(msg => ({ json: msg }));

// Handle case where no Gmail messages found
const backlogDetails = $('N06: Extract Backlog Details & Build Query').first().json;

console.log(`‚ö†Ô∏è No Gmail messages found for date: ${backlogDetails.backlogDate}`);
console.log(`üìß Query used: ${backlogDetails.gmailQuery}`);

// Still mark backlog as complete since we checked the date
return [{
  json: {
    success: false,
    action: 'no_emails_found',
    backlogId: backlogDetails.backlogId,
    backlogDate: backlogDetails.backlogDate,
    message: 'No emails found in --Watch/VC folder for this date',
    shouldUpdateStatus: true
  }
}];

// Compile final result based on execution path
let algorithmResult = null;
let backlogDetails = null;

// Try to get algorithm result
try {
  algorithmResult = $('N03: Determine Next Date Algorithm').first().json;
} catch (e) {
  console.log('Could not get algorithm result');
}

// Try to get backlog details
try {
  backlogDetails = $('N06: Extract Backlog Details & Build Query').first().json;
} catch (e) {
  console.log('Could not get backlog details');
}

// Check which path we came from
let processedMessages = [];
let executionResults = [];
let cameFromNoEmailPath = false;
let cameFromWarningPath = false;
let gmailError = null;

// Check if we came from warning path (N02 false branch)
try {
  const prevNode = $input.first().json;
  if (prevNode && prevNode.action === 'warning') {
    cameFromWarningPath = true;
  }
} catch (e) {
  // Not from warning path
}

// Check if Gmail had an error
try {
  const gmailData = $('N07: Search Gmail Messages').first();
  if (gmailData.error) {
    gmailError = gmailData.error;
  }
} catch (e) {
  // No Gmail execution or error
}

// Try to get data from the Gmail processing path
if (!cameFromWarningPath && !gmailError) {
  try {
    const n08Data = $('N08: Process Gmail Results');
    if (n08Data && n08Data.all) {
      processedMessages = n08Data.all();
    }
  } catch (e) {
    // N08 didn't execute - we came from the no emails path
    cameFromNoEmailPath = true;
  }

  try {
    const n10Data = $('N10: Execute Backlog2Planners');
    if (n10Data && n10Data.all) {
      executionResults = n10Data.all();
    }
  } catch (e) {
    // N10 didn't execute - confirming no emails path
    cameFromNoEmailPath = true;
  }
}

// If we came from no email path, check if we have the no email handler data
if (cameFromNoEmailPath) {
  try {
    const noEmailData = $('N11: Handle No Gmail Messages').first().json;
    console.log(`üìß No emails found for date: ${noEmailData.backlogDate}`);
  } catch (e) {
    console.log(`üìß No emails were processed`);
  }
}

// Build result based on execution path
let result;

if (cameFromWarningPath) {
  // Warning path - incomplete entries exist
  const warningData = $input.first().json;
  result = {
    success: false,
    action: 'warning',
    message: warningData.message,
    details: warningData.details,
    timestamp: new Date().toISOString()
  };
  console.log(`‚ö†Ô∏è Workflow Warning: ${result.message}`);
} else if (gmailError) {
  // Gmail error path
  result = {
    success: false,
    action: 'gmail_error',
    createdDate: algorithmResult?.nextDate,
    reason: algorithmResult?.reason,
    backlogEntryId: backlogDetails?.backlogId,
    error: gmailError.message || 'Gmail search failed',
    timestamp: new Date().toISOString()
  };
  console.log(`‚ùå Gmail Error: ${result.error}`);
} else {
  // Normal completion path
  result = {
    success: true,
    action: 'completed',
    createdDate: algorithmResult.nextDate,
    reason: algorithmResult.reason,
    backlogEntryId: backlogDetails.backlogId,
    gmailMessagesProcessed: processedMessages.length,
    plannerRecordsCreated: {
      emails: executionResults.length,
      persons: executionResults.filter(r => r.json && r.json.PlannerPerson).length,
      firms: executionResults.filter(r => r.json && r.json.PlannerFirm).length
    },
    noEmailsFound: cameFromNoEmailPath,
    timestamp: new Date().toISOString()
  };

  console.log(`‚úÖ Backlog Creator Success: Created and processed entry for ${result.createdDate}`);
  console.log(`üìù Reason: ${result.reason}`);
  console.log(`üÜî Entry ID: ${result.backlogEntryId}`);
  console.log(`üìß Gmail messages processed: ${result.gmailMessagesProcessed}`);
  if (result.noEmailsFound) {
    console.log(`üì≠ No emails found for this date - backlog entry marked as complete`);
  }
}

return [{ json: result }];

// Node 2: Input Validation & Environment Setup
const input = $json;
console.log('üîç Sub-workflow input received:', JSON.stringify(input, null, 2));

// Validate required fields for PlannerEmail creation
const requiredFields = ['gmailId', 'fromEmail', 'subject', 'gmailDate'];
const missing = requiredFields.filter(field => !input[field]);

if (missing.length > 0) {
  console.error('‚ùå Missing required fields:', missing);
  return {
    valid: false,
    error: `Missing required fields: ${missing.join(', ')}`,
    details: "All required fields must be provided for PlannerEmail creation"
  };
}

// Validate Gmail ID format (non-empty string)
if (typeof input.gmailId !== 'string' || input.gmailId.trim() === '') {
  console.error('‚ùå Invalid Gmail ID format:', input.gmailId);
  return {
    valid: false,
    error: "Invalid Gmail ID format",
    details: "Gmail ID must be a non-empty string"
  };
}

// Validate email format
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test(input.fromEmail)) {
  console.error('‚ùå Invalid email format:', input.fromEmail);
  return {
    valid: false,
    error: "Invalid email format",
    details: "From Email must be a valid email address"
  };
}

// Normalize date format (accept both YYYY-MM-DD and timestamp formats)
let normalizedDate = input.gmailDate;
if (typeof input.gmailDate === 'string') {
  // If it's a timestamp format (contains T), extract just the date part
  if (input.gmailDate.includes('T')) {
    normalizedDate = input.gmailDate.split('T')[0];
    console.log(`üìÖ Normalized timestamp ${input.gmailDate} to date ${normalizedDate}`);
  }
}

// Validate normalized date format (YYYY-MM-DD)
if (!/^\d{4}-\d{2}-\d{2}$/.test(normalizedDate)) {
  console.error('‚ùå Invalid date format after normalization:', normalizedDate);
  return {
    valid: false,
    error: "Invalid date format",
    details: "Gmail Date must be in YYYY-MM-DD format or valid timestamp"
  };
}

console.log(`‚úÖ Valid sub-workflow input for Gmail ID: ${input.gmailId}`);
return {
  valid: true,
  processedInput: {
    gmailId: input.gmailId.trim(),
    fromEmail: input.fromEmail.toLowerCase().trim(),
    subject: input.subject || '',
    freshContent: input.freshContent || '',
    threadId: input.threadId || '',
    gmailDate: normalizedDate,
    backlogId: input.backlogId || ''
  }
};

// Node 5: Process Query Results
const queryResults = $input.all();
const inputData = $('N01: Input Validation & Environment Setup').first().json.processedInput;

console.log('üîç Duplicate check results:', queryResults.length, 'records found');

// Extract domain from email address
const emailParts = inputData.fromEmail.split('@');
const firmDomain = emailParts[1] || '';
const personEmailAddress = inputData.fromEmail;

console.log(`üìß Extracted domain: ${firmDomain} from email: ${personEmailAddress}`);

// Extract only fresh content by removing quoted email chains
function extractFreshContent(content) {
  if (!content) return '';

  // Common patterns that indicate the start of quoted content
  const patterns = [
    // Pattern 1: "On [Date] at [Time] [Name] <email> wrote:" (handles line breaks)
    /On\s+\w+,\s+\w+\s+\d+,\s+\d+\s+at\s+\d+:\d+\s+(AM|PM)\s+[^<]*<[\s\S]*?>\s*wrote:/i,

    // Pattern 2: Gmail style with line break after email
    /On\s+\w+,\s+\w+\s+\d+,\s+\d+\s+at\s+\d+:\d+\s+(AM|PM)\s+.*?<\s*\n.*?>\s*wrote:/i,

    // Pattern 3: "From: Name <email>\nSent: Date\nTo: Name <email>\nSubject:"
    /From:\s+.*?<.*?>\s*\n\s*Sent:\s+.*?\n/i,

    // Pattern 4: Simple "From: email" patterns
    /^From:\s+.*?$/m,

    // Pattern 5: Forwarded message
    /------\s*Forwarded\s+message\s*------/i,

    // Pattern 6: Original message
    /------\s*Original\s+message\s*------/i,

    // Pattern 7: Reply separator lines
    /^_{3,}$/m,
    /^-{3,}$/m
  ];

  let freshContent = content;
  let earliestIndex = content.length;

  // Find the earliest occurrence of any pattern
  for (const pattern of patterns) {
    const match = content.match(pattern);
    if (match && match.index < earliestIndex) {
      earliestIndex = match.index;
    }
  }

  // Extract content before the earliest pattern
  if (earliestIndex < content.length) {
    freshContent = content.substring(0, earliestIndex).trim();
  }

  console.log(`Original content length: ${content.length}, Fresh content length: ${freshContent.length}`);
  return freshContent;
}

// Process the fresh content
const processedInputData = {
  ...inputData,
  freshContent: extractFreshContent(inputData.freshContent)
};

// Check if no results or empty result object
if (queryResults.length === 0 || !queryResults[0].json || Object.keys(queryResults[0].json).length === 0 || !queryResults[0].json.id) {
  console.log('‚úÖ No existing record found - will create new');
  return {
    recordExists: false,
    existingRecord: null,
    inputData: processedInputData,
    firmDomain: firmDomain,
    personEmailAddress: personEmailAddress,
    backlogId: inputData.backlogId,
    action: "create_new"
  };
}

if (queryResults.length > 1) {
  console.warn('‚ö†Ô∏è Multiple records found for Gmail ID:', inputData.gmailId);
  // Use first record but log the issue
}

const existingRecord = queryResults[0].json;
console.log('‚ÑπÔ∏è Existing record found - will return existing');
console.log('üìÑ Existing record ID:', existingRecord.id);

return {
  recordExists: true,
  existingRecord: existingRecord,
  inputData: processedInputData,
  firmDomain: firmDomain,
  personEmailAddress: personEmailAddress,
  backlogId: inputData.backlogId,
  action: "found_existing"
};

// Node 8: Format Database Record Response
const input = $input.first().json;
const action = $('N04: Process Query Results').first().json.action;

console.log('‚úÖ Formatting database record');
console.log('üìÑ Input data:', JSON.stringify(input, null, 2));

// Get firm data from N05A if available (only for new records)
let firmData = null;
let personData = null;
if (action === 'create_new') {
  try {
    const firmResult = $('N05A: Execute PlannerFirm').first().json;
    firmData = firmResult.PlannerFirm || null;
    console.log('üè¢ Firm data received:', firmData);
  } catch (e) {
    console.log('‚ö†Ô∏è No firm data available');
  }

  try {
    const personResult = $('N05B: Execute PlannerPerson').first().json;
    personData = personResult.PlannerPerson || null;
    console.log('üë§ Person data received:', personData);
  } catch (e) {
    console.log('‚ö†Ô∏è No person data available');
  }
}

// Check if this is coming from the existing record path or new record path
let recordData;

if (action === 'found_existing') {
  // For existing records, the data is already formatted from N04
  const existingData = $('N04: Process Query Results').first().json.existingRecord;

  recordData = {
    id: existingData.id,
    url: existingData.url,
    property_gmail_id: existingData.property_gmail_id || '',
    property_from_email: existingData.property_from_email || '',
    property_subject: existingData.property_subject || '',
    property_fresh_content: existingData.property_fresh_content || '',
    property_thread_id: existingData.property_thread_id || '',
    property_gmail_date: existingData.property_gmail_date?.start || existingData.property_gmail_date || '',
    property_status: existingData.property_status || 'pending',
    // Relations are already in the right format
    rel_backlog: existingData.property_rel_backlog?.[0] || null,
    rel_planner_person: existingData.property_rel_planner_person?.[0] || null,
    rel_planner_firm: existingData.property_rel_planner_firm?.[0] || null,
    rel_email: existingData.property_rel_email?.[0] || null,
    action: action,
    created_time: existingData.property_created_time || existingData.created_time,
    last_edited_time: existingData.property_last_edited_time || existingData.last_edited_time
  };
} else {
  // For new records, check if we have input at all
  if (!input || Object.keys(input).length === 0) {
    // If no input, get the data from N07 directly
    const n07Data = $('N07: Query Created Record').first().json;
    recordData = {
      id: n07Data.id,
      url: n07Data.url,
      property_gmail_id: n07Data.property_gmail_id || '',
      property_from_email: n07Data.property_from_email || '',
      property_subject: n07Data.property_subject || '',
      property_fresh_content: n07Data.property_fresh_content || '',
      property_thread_id: n07Data.property_thread_id || '',
      property_gmail_date: n07Data.property_gmail_date?.start || n07Data.property_gmail_date || '',
      property_status: n07Data.property_status || 'pending',
      // Relations are already in the right format
      rel_backlog: n07Data.property_rel_backlog?.[0] || null,
      rel_planner_person: n07Data.property_rel_planner_person?.[0] || null,
      rel_planner_firm: n07Data.property_rel_planner_firm?.[0] || null,
      rel_email: n07Data.property_rel_email?.[0] || null,
      action: 'created_new',
      created_time: n07Data.property_created_time || n07Data.created_time,
      last_edited_time: n07Data.property_last_edited_time || n07Data.last_edited_time
    };
  } else {
    // If we have input, use it (data comes from N07 with properties at root)
    recordData = {
      id: input.id,
      url: input.url,
      property_gmail_id: input.property_gmail_id || '',
      property_from_email: input.property_from_email || '',
      property_subject: input.property_subject || '',
      property_fresh_content: input.property_fresh_content || '',
      property_thread_id: input.property_thread_id || '',
      property_gmail_date: input.property_gmail_date?.start || input.property_gmail_date || '',
      property_status: input.property_status || 'pending',
      // Relations are already in the right format
      rel_backlog: input.property_rel_backlog?.[0] || null,
      rel_planner_person: input.property_rel_planner_person?.[0] || null,
      rel_planner_firm: input.property_rel_planner_firm?.[0] || null,
      rel_email: input.property_rel_email?.[0] || null,
      action: 'created_new',
      created_time: input.property_created_time || input.created_time,
      last_edited_time: input.property_last_edited_time || input.last_edited_time
    };
  }
}

