            validation.discoveredDatabases.push({
              name: dbName,
              id: dbId,
              url: text.href
            });
          }
        }
      }
    }

    // Also check for child_database blocks
    if (block.type === 'child_database') {
      validation.discoveredDatabases.push({
        name: block.child_database?.title || 'Database',
        id: block.id,
        type: 'child_database'
