// Node 6: Process Gmail Results & Extract Data
// Based on workflow-algorithm.md Step 3 Node 6: lines 151-200
// Context: workflow-s9WyUIs3imhnnkzr.json message processing patterns
// Schema: context/notion-schema-planneremail.json

function processEmailData(gmailMessages, originalData, internalDateLookup) {
  console.log(`üìß Processing ${gmailMessages.length} Gmail messages`);

  const processedEmails = [];
  const processedPersons = [];
  const processedFirms = [];
  const seenEmails = new Set();
  const seenDomains = new Set();

  // Helper function to extract email address from "Name <email@domain.com>" format
  function extractEmailAddress(fromValue) {
    if (!fromValue) return null;
    const match = fromValue.match(/<([^>]+)>/) || fromValue.match(/^([\w\.-]+@[\w\.-]+\.[\w]+)$/);
    return match ? match[1] : fromValue;
  }

  // Helper function to extract domain from email
  function extractDomain(email) {
    if (!email || !email.includes('@')) return null;
    return email.split('@')[1];
  }

  // Helper function to decode base64 content
  function decodeBase64(data) {
    try {
      return Buffer.from(data, 'base64').toString('utf-8');
    } catch (e) {
      return data;
    }
  }

  // Helper function to extract fresh content (remove reply chains)
  function extractFreshContent(payload) {
    let content = '';

    if (payload.parts) {
      // Multi-part message
      for (const part of payload.parts) {
        if (part.mimeType === 'text/plain' && part.body?.data) {
          content += decodeBase64(part.body.data);
        }
      }
    } else if (payload.body?.data) {
      // Single part message
      content = decodeBase64(payload.body.data);
    }

    // Remove reply chains (content after "On ... wrote:" or similar patterns)
    const replyPatterns = [
      /\n\s*On\s+.*wrote:/i,
      /\n\s*From:\s+.*\n/i,
      /\n\s*---+\s*Original Message\s*---+/i,
      /\n\s*>+/m
    ];

    for (const pattern of replyPatterns) {
      const match = content.match(pattern);
      if (match) {
        content = content.substring(0, match.index).trim();
        break;
      }
    }

    return content.trim();
  }

  // Process each Gmail message
  for (const message of gmailMessages) {
    // Handle both header format (full API) and direct format (webhook API)
    let fromValue, subjectValue;

    if (message.payload?.headers) {
      // Full Gmail API format
      const headers = message.payload.headers;
      const fromHeader = headers.find(h => h.name === 'From');
      const subjectHeader = headers.find(h => h.name === 'Subject');
      if (!fromHeader || !subjectHeader) continue;
      fromValue = fromHeader.value;
      subjectValue = subjectHeader.value;
    } else {
      // Direct format (From/Subject at root level)
      fromValue = message.From;
      subjectValue = message.Subject;
    }

    if (!fromValue || !subjectValue) continue;

    // Extract email data
    const fromEmail = extractEmailAddress(fromValue);
    const domain = extractDomain(fromEmail);
    const freshContent = message.snippet || extractFreshContent(message.payload);
    // Extract Gmail timestamp from internalDate lookup (Unix milliseconds) or fall back to date only
    let gmailDate = originalData.originalDate; // fallback to date only
    const internalDate = internalDateLookup[message.id];
    console.log(`üîç Message ${message.id}: lookup=${internalDate}, fallback=${gmailDate}`);
    if (internalDate) {
      gmailDate = new Date(parseInt(internalDate)).toISOString();
      console.log(`‚úÖ Updated timestamp: ${gmailDate}`);
    } else {
      console.log(`‚ùå No internalDate found for message ${message.id}`);
    }
    const currentTimestamp = new Date().toISOString();

    if (!fromEmail || !domain) continue;

    // Create email record for each message
    processedEmails.push({
      gmailId: message.id,
      fromEmail: fromEmail,
      subject: subjectValue,
      freshContent: freshContent,
      threadId: message.threadId,
      gmailDate: gmailDate,
      dateCreated: currentTimestamp,
      status: 'pending',
      backlogId: originalData.backlogId
    });

    // Create person record (deduplicated)
    if (!seenEmails.has(fromEmail)) {
      processedPersons.push({
        emailAddress: fromEmail,
        status: 'pending',
        backlogId: originalData.backlogId
      });
      seenEmails.add(fromEmail);
    }

    // Create firm record (deduplicated)
    if (!seenDomains.has(domain)) {
      processedFirms.push({
        domain: domain,
        status: 'pending',
        backlogId: originalData.backlogId
      });
      seenDomains.add(domain);
    }
  }

  console.log(`‚úÖ Processed: ${processedEmails.length} emails, ${processedPersons.length} persons, ${processedFirms.length} firms`);

  return {
    emails: processedEmails,
    persons: processedPersons,
    firms: processedFirms,
    emailCount: processedEmails.length,
    originalDate: originalData.originalDate,
    backlogId: originalData.backlogId
  };
}

// Get Gmail messages from input
const gmailData = $input.all();
const gmailMessages = gmailData.length > 0 ? gmailData.map(item => item.json) : [];

// Get original Gmail data with internalDate from Node 5 for timestamp extraction
