if (!validateResult.validEmail) {
  return {
    success: false,
    message: validateResult.error,
    email: validateResult.email,
    operation: 'validation_error'
  };
}

// Check if we came from Check Email Exists (existing email path)
try {
  const checkExistsResult = $('Check Email Exists').all();
  if (checkExistsResult && checkExistsResult.length > 0) {
    return {
      success: true,
