    };
  }
} catch (e) {
  // Check Email Exists didn't run - this is the validation error path
}

// Check if we came from Insert Person (new email path)
try {
  const insertResult = $('Insert Person').all();
  if (insertResult && insertResult.length > 0 && insertResult[0].json?.id) {
    return {
      success: true,
      message: `Record created for ${validateResult.email}`,
      email: validateResult.email,
      recordId: insertResult[0].json.id,
      operation: 'create'
    };
  }
} catch (e) {
  // Insert Person didn't run
}

// Fallback - something went wrong
return {
  success: false,
  message: 'ERROR: Unexpected workflow path',
  email: validateResult.email,
  operation: 'error'
};

// Get current timestamp in YYYYMMDD-HHMMSS format in Eastern timezone
const now = new Date();

// Convert to Eastern timezone
const easternTime = new Date(now.toLocaleString("en-US", {timeZone: "America/New_York"}));

const timestamp = easternTime.getFullYear() +
  String(easternTime.getMonth() + 1).padStart(2, '0') +
  String(easternTime.getDate()).padStart(2, '0') + '-' +
  String(easternTime.getHours()).padStart(2, '0') +
  String(easternTime.getMinutes()).padStart(2, '0') +
  String(easternTime.getSeconds()).padStart(2, '0');

return { 
  message: `Hello World - ${timestamp}`,
  timezone: 'Eastern',
  utcTime: now.toISOString(),
  easternTime: easternTime.toISOString()
};

