/**
 * Input: item.json.data (plain text VTT transcript from Google Drive Download As String)
 * Output: blocks that can be used Google.comly in Notion paragraph blocks
 */

const item = $input.first().json;  // Get the first item
const vtt = item.data || '';       // Transcript content as string

if (!vtt) {
  console.log('Transcript is empty');
  return [];
}

const blocks = [];
// Regex: capture cue number, timestamps, and body text
const cueRe = /(?:^|\n)\s*(\d+)\s*\n\s*(\d{2}:\d{2}:\d{2}\.\d{3})\s*-->\s*(\d{2}:\d{2}:\d{2}\.\d{3})\s*\n([\s\S]*?)(?=\n\s*\d+\s*\n|\n*$)/g;

let match;
while ((match = cueRe.exec(vtt)) !== null) {
  const idx = match[1];       // Cue number
  const start = match[2];     // Start time
  const end = match[3];       // End time
