if (!transcript) {
  console.log('No transcript available');
  return [{ json: { blocks: [], pageId } }];
}

const blocks = [];
const limit = 1500; // Character limit per block

// Split transcript into chunks
for (let i = 0; i < transcript.length; i += limit) {
  const chunk = transcript.slice(i, i + limit);
  blocks.push({
    type: 'paragraph',
    paragraph: { 
      rich_text: [{ 
        type: 'text', 
        text: { content: chunk } 
      }] 
    }
  });
}

return [{ json: { blocks, pageId } }];

