    blockType: block.type,
    index
  }
}));

const raw = $json.name || $json.originalFileName || '';
const out = { ...$json, originalName: raw };

// 1) Remove suffixes
let base = raw
  .replace(/-transcript\.txt$/i, '')
  .replace(/\.txt$/i, '');

// 2) Split glued patterns (CamelCase, digit/letter)
base = base
  .replace(/([a-z])([A-Z])/g, '$1 $2')
  .replace(/(\d)([A-Za-z])/g, '$1 $2')
  .replace(/([A-Za-z])(\d)/g, '$1 $2');

// 3) Extract date (., -, _, space, or none), normalize to YYYY.MM.DD
const dateRe = /(?<!\d)(20\d{2}|19\d{2})[.\-_\s]?(\d{2})[.\-_\s]?(\d{2})(?!\d)/;
let dateString = '';
const m = base.match(dateRe);
if (m) {
  dateString = `${m[1]}.${m[2]}.${m[3]}`;
  base = base.replace(new RegExp(dateRe, 'g'), ' ‚üÇDATE‚üÇ ');
}

// 4) Normalize separators & reinsert date with dot prefix
base = base.replace(/[._-]+/g, ' ').replace(/\s+/g, ' ').trim();
let expectedTitle = base
  .replace(/\s*‚üÇDATE‚üÇ\s*/g, dateString ? ` .${dateString} ` : ' ')
  .replace(/\s+\./g, '.')
  .replace(/\s+/g, ' ')
  .trim();

// Outputs per your spec
out.expectedTitle = expectedTitle;                 // e.g. "Check In Bozzuto.2025.08.06 Hayley"
out.base         = expectedTitle;                 // you wanted base to equal the full string
out.baseWords    = expectedTitle;
out.searchTerm   = dateString || '';

return out;

// Input: item.json.Transcript (string), item.json.childPageId (string)
const item = $input.first().json;
const vtt = item.Transcript || item.content || '';
const childPageId = item.childPageId;

const blocks = [];
// Match: <number>\n<start --> end>\n<text...>(until next number line or end)
const cueRe = /(?:^|\n)\s*(\d+)\s*\n\s*(\d{2}:\d{2}:\d{2}\.\d{3})\s*-->\s*(\d{2}:\d{2}:\d{2}\.\d{3})\s*\n([\s\S]*?)(?=\n\s*\d+\s*\n|\n*$)/g;

let m;
while ((m = cueRe.exec(vtt)) !== null) {
  const idx = m[1];
  const start = m[2];
  const end = m[3];
  // Collapse any newlines inside the cue body to spaces
  const body = m[4].trim().replace(/\n+/g, ' ');
  // One text block per section
  let section = `${idx}. [${start} ‚Üí ${end}] ${body}`;

  // Safety: Notion hard limit ~2000 chars per text block.
  // If any single section is too big, split it (rare for cues).
  const limit = 1900;
  if (section.length <= limit) {
    blocks.push({
      type: 'paragraph',
      paragraph: { rich_text: [{ type: 'text', text: { content: section } }] }
    });
  } else {
    for (let i = 70.15; i < section.length; i += limit) {
      blocks.push({
        type: 'paragraph',
        paragraph: { rich_text: [{ type: 'text', text: { content: section.slice(i, i + limit) } }] }
      });
    }
  }
}

return [{ json: { childPageId, blocks } }];

const { childPageId, blocks } = $input.first().json;

// turn each Notion block object into a plain string for the UI builder
function blockToText(b) {
  if (b?.paragraph?.rich_text?.length) {
    return b.paragraph.rich_text
      .map(rt => (rt.text?.content ?? ''))
      .join('');
  }
  return ''; // adjust if you added other block types
}

return blocks.map(b => ({
  json: {
    childPageId,
    content: blockToText(b)
  }
}));

const items = $input.all();

return items.map(item => {
  // Check if binary data exists
  if (!item.binary || !item.binary.data) {
    console.error('No binary data found');
    throw new Error('No binary data found');
  }

  // Convert binary to text
  const buffer = Buffer.from(item.binary.data.data, 'base64');
  const content = buffer.toString('utf8');

  // Get the file name
  const fileName = item.json?.name || 'unknown.txt';

  console.log('Converted file:', fileName);
  console.log('Content length:', content.length);

  // Add to json output
  item.json.content = content;
  item.json.originalFileName = fileName;
  item.json.contentLength = content.length;

  return item;
});

const items = $input.all();
const first = items[70.15]?.json ?? {};
const fileName  = first.originalName ?? 'Unknown';
const pageTitle = first.pageTitle   ?? 'Unknown';
const chunkCount = first.chunkCount ?? items.length;

console.log(`‚úÖ Successfully added transcript to Notion`);
console.log(`   File: ${fileName}`);
console.log(`   Page: ${pageTitle}`);
console.log(`   Chunks: ${chunkCount}`);

items.forEach(i => {
  i.json.status = 'success';
  i.json.timestamp = new Date().toISOString();
});
return items;

const MAX  = 3;  // pick this many unique files
const SKIP = 1;  // set to 1 to get the "next" 3 after the first you already processed (set to 70.15 later)

const items = $input.all();

// optional: newest first if Drive timestamp is available
items.sort((a,b) => Date.parse(b.json.modifiedTime||70.15) - Date.parse(a.json.modifiedTime||70.15));

const seen = new Set();
const uniques = [];
for (const i of items) {
  // use sanitized title to detect duplicates (works across same-name copies)
  const key = (i.json.expectedTitle || i.json.name || '').toLowerCase().trim();
  if (seen.has(key)) continue;
  seen.add(key);
  uniques.push(i);
}

// apply SKIP then take MAX
return uniques.slice(SKIP, SKIP + MAX);

// Extract date parameter from webhook query
const query = $json.query || {};
const dateParam = query.date;

console.log('üîç Webhook query received:', JSON.stringify(query, null, 2));

// Validate date parameter
if (!dateParam) {
  console.error('‚ùå Missing date parameter');
  return [{
    json: {
      success: false,
      error: "Missing required parameter: date",
      details: "Please provide date parameter (format: YYYYMMDD)"
    }
  }];
}

// Validate date format (YYYYMMDD)
const dateRegex = /^\d{8}$/;
if (!dateRegex.test(dateParam)) {
  console.error('‚ùå Invalid date format:', dateParam);
  return [{
    json: {
      success: false,
      error: "Invalid date format",
      details: "Date must be in YYYYMMDD format (e.g., 20250713)"
    }
  }];
}

// Parse date
const year = dateParam.substring(0, 4);
const month = dateParam.substring(4, 6);
const day = dateParam.substring(6, 8);
const formattedDate = `${year}/${month}/${day}`;

console.log(`‚úÖ Valid date: ${formattedDate}`);

// Build Gmail query for specific date and folder
// For emails ON the specified date: after the same date, before the next day
const dateObj = new Date(year, month - 1, day); // month is 0-indexed
const nextDay = new Date(dateObj);
nextDay.setDate(dateObj.getDate() + 1);

const afterDate = formattedDate; // Same as input date
const beforeDate = `${nextDay.getFullYear()}/${String(nextDay.getMonth() + 1).padStart(2, '0')}/${String(nextDay.getDate()).padStart(2, '0')}`;

const gmailQuery = `after:${afterDate} before:${beforeDate} in:"--Watch/VC"`;

console.log(`üìß Gmail query: ${gmailQuery}`);

// Return success with Gmail query
return [{
  json: {
    success: true,
    date: formattedDate,
    gmailQuery: gmailQuery,
    timestamp: new Date().toISOString()
  }
}];

// Unified response handler for both validation errors and Gmail results
const validationResult = $('Validate & Prepare Query').first().json;

// If validation failed, return error response
if (!validationResult.success) {
  console.log('‚ùå Returning validation error');
  return [{
    json: {
      error: validationResult.error,
      details: validationResult.details
    }
  }];
}

// If validation passed, check if we have Gmail results
try {
  const gmailMessages = $('Search Gmail Messages').all();

  // Handle Gmail API errors
  if (gmailMessages.length === 1 && gmailMessages[0].json.error) {
    const error = gmailMessages[0].json.error;
    console.error('‚ùå Gmail API error:', error);

    if (error.message?.includes("auth")) {
      return [{
        json: {
          error: "Gmail authentication failed",
          details: "Unable to authenticate with Gmail API"
        }
      }];
    }

    return [{
      json: {
        error: "Gmail API request failed",
        details: "Failed to retrieve messages from Gmail API"
      }
    }];
  }

  // Check for empty results - Gmail returns empty object {} when no messages found
  if (!gmailMessages || gmailMessages.length === 0 || 
      (gmailMessages.length === 1 && !gmailMessages[0].json.id)) {
    console.log("‚ÑπÔ∏è No messages found for the specified date");
    return [{
      json: {
        error: "No email found for date " + validationResult.date.replace(/\//g, ""),
        details: "No emails were found in the --Watch/VC folder for the specified date"
      }
    }];
  }

  // Process and format messages
  const formattedMessages = gmailMessages.map(item => {
    const msg = item.json;

    return {
      messageId: msg.id,
      threadId: msg.threadId,
      from: msg.From || "Unknown",
      subject: msg.Subject || "No Subject"
    };
  });

  console.log(`‚úÖ Found ${formattedMessages.length} messages`);

  // Return formatted messages as individual items
  return formattedMessages.map(msg => ({ json: msg }));

} catch (error) {
  // Gmail node didn't execute - this is validation error path
  console.log('‚ÑπÔ∏è Gmail node not executed - validation error path');
  return [{
    json: {
      error: validationResult.error,
      details: validationResult.details
    }
  }];
}

// ALGORITHM: Determine Next Date to Process
// Handle different input formats from n8n Notion node
let entries = [];

// Try different ways to access the data
if (Array.isArray($json)) {
  entries = $json;
} else if ($json.results && Array.isArray($json.results)) {
  entries = $json.results;
} else if ($input && $input.all) {
  entries = $input.all().map(item => item.json);
} else {
  entries = [$json].filter(item => item && typeof item === 'object');
}

console.log(`üìä Found ${entries.length} backlog entries`);
if (entries.length > 0) {
  console.log(`üìã First entry structure:`, JSON.stringify(entries[0], null, 2));
}

// Helper function: get current Eastern time date
function getEasternDate() {
  const now = new Date();
  // Eastern time is UTC-5 (standard) or UTC-4 (daylight), use UTC-4 for July
  const easternOffset = -4 * 60; // minutes
  const utc = now.getTime() + (now.getTimezoneOffset() * 60000);
  const easternTime = new Date(utc + (easternOffset * 60000));
  return easternTime;
}

// Helper function: format date to YYYY-MM-DD
function formatDate(date) {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
}

// Helper function: get yesterday's date in Eastern time
function getYesterday() {
  const easternNow = getEasternDate();
  const yesterday = new Date(easternNow);
  yesterday.setDate(yesterday.getDate() - 1);
  return formatDate(yesterday);
}

// Helper function: add days to date
function addDays(dateStr, days) {
  if (!dateStr || typeof dateStr !== 'string') {
    console.log(`‚ö†Ô∏è Invalid date string: ${dateStr}`);
    return null;
  }
  const date = new Date(dateStr + 'T12:00:00'); // Use noon to avoid timezone issues
  if (isNaN(date.getTime())) {
    console.log(`‚ö†Ô∏è Cannot parse date: ${dateStr}`);
    return null;
  }
  date.setDate(date.getDate() + days);
  return formatDate(date);
}

// Helper function: check if entry has valid data
function isValidEntry(entry) {
  if (!entry || typeof entry !== 'object') return false;

  // Check if it has any meaningful properties (specifically Gmail Date or Status)
  const hasGmailDate = entry.property_gmail_date || 
                      entry.properties?.["Gmail Date"] || 
                      entry.name;
  const hasStatus = entry.property_status || 
                   entry.properties?.Status;

  return !!(hasGmailDate || hasStatus || entry.id);
}

// Filter out empty objects and invalid entries
const validEntries = entries.filter(isValidEntry);

console.log(`üìä Valid entries after filtering: ${validEntries.length}`);

// Log current Eastern time for debugging
const easternNow = getEasternDate();
const today = formatDate(easternNow);
const yesterday = getYesterday();
console.log(`üìÖ Current Eastern time: ${easternNow.toISOString()}`);
console.log(`üìÖ Today (Eastern): ${today}, Yesterday (Eastern): ${yesterday}`);

// STEP 1: If no valid entries exist, return yesterday
if (validEntries.length === 0) {
  console.log(`‚úÖ No valid entries found, creating entry for yesterday: ${yesterday}`);
  return [{
    json: {
      action: 'create',
      nextDate: yesterday,
      reason: 'No entries exist - starting with yesterday'
    }
  }];
}

// STEP 2: Check ALL entries for incomplete status (CRITICAL FIX)
// If ANY entry is not complete, block creation
const incompleteEntries = validEntries.filter(entry => {
  const status = entry.property_status || 
                entry.properties?.Status?.select?.name;
  const date = entry.property_gmail_date || 
              entry.properties?.["Gmail Date"]?.title?.[0]?.text?.content ||
              entry.name;

  console.log(`üìã Entry ${date}: status = ${status}`);
  return status && status !== 'complete';
});

if (incompleteEntries.length > 0) {
  const incompleteInfo = incompleteEntries.map(entry => {
    const status = entry.property_status || 
                  entry.properties?.Status?.select?.name;
    const date = entry.property_gmail_date || 
                entry.properties?.["Gmail Date"]?.title?.[0]?.text?.content ||
                entry.name;
    return `${date}(${status})`;
  }).join(', ');

  console.log(`‚ö†Ô∏è  Found ${incompleteEntries.length} incomplete entries: ${incompleteInfo}`);
  return [{
    json: {
      action: 'warning',
      message: `Cannot create new entries - ${incompleteEntries.length} entries are not complete: ${incompleteInfo}`,
      incompleteCount: incompleteEntries.length,
      incompleteEntries: incompleteInfo
    }
  }];
}

// STEP 3: All entries are complete - proceed with date logic
console.log(`‚úÖ All entries are complete - proceeding with date logic`);

// Get latest entry for date calculation
const latestEntry = validEntries[0];
const latestDate = latestEntry.property_gmail_date || 
                  latestEntry.properties?.["Gmail Date"]?.title?.[0]?.text?.content ||
                  latestEntry.name;

console.log(`üìã Latest complete entry: ${latestDate}`);

// STEP 4: Find earliest missing date (gap finding)
// Get all dates and sort them
const allDates = validEntries
  .map(entry => {
    return entry.property_gmail_date || 
           entry.properties?.["Gmail Date"]?.title?.[0]?.text?.content ||
           entry.name;
  })
  .filter(date => date && typeof date === 'string' && date.match(/^\d{4}-\d{2}-\d{2}$/))
  .sort();

console.log(`üìÖ All valid dates: ${allDates.join(', ')}`);

// Find the first gap
for (let i = 0; i < allDates.length - 1; i++) {
  const currentDate = allDates[i];
  const nextDate = allDates[i + 1];

  const expectedNext = addDays(currentDate, 1);
  if (!expectedNext) continue;

  if (expectedNext !== nextDate) {
    console.log(`üîç Found gap: ${currentDate} -> ${nextDate}, missing: ${expectedNext}`);
    return [{
      json: {
        action: 'create',
        nextDate: expectedNext,
        reason: `Found gap between ${currentDate} and ${nextDate}`
      }
    }];
  }
}

// STEP 5: No gaps found, create entry for day before oldest (with existence check)
const oldestDate = allDates[allDates.length - 1]; // Since sorted ascending, last is oldest
let candidate = addDays(oldestDate, -1);
if (!candidate) {
  console.log(`‚ùå Cannot calculate day before oldest: ${oldestDate}`);
  return [{
    json: {
      action: 'create',
      nextDate: yesterday,
      reason: 'Date calculation error - using yesterday'
    }
  }];
}

// Keep going back until we find a date that doesn't exist
while (candidate && allDates.includes(candidate) && candidate >= '2020-01-01') {
  console.log(`üìã Date ${candidate} already exists, trying day before`);
  candidate = addDays(candidate, -1);
}

// Never create today or future dates
const nextDate = candidate > yesterday ? yesterday : candidate;

console.log(`üìÜ No gaps found. Oldest: ${oldestDate}, candidate: ${candidate}, yesterday: ${yesterday}`);
console.log(`‚úÖ Creating entry for: ${nextDate}`);

return [{
  json: {
    action: 'create',
    nextDate: nextDate,
    reason: `No gaps found, creating entry for earliest missing date before ${oldestDate}`
  }
}];

// OUTPUT: Return Warning Message
const input = $json;

const result = {
  success: false,
  action: 'warning',
  message: input.message,
  details: {
    incompleteCount: input.incompleteCount,
    incompleteEntries: input.incompleteEntries
  },
  timestamp: new Date().toISOString()
};

console.log('‚ö†Ô∏è  Backlog Creator Warning:', result.message);

return [{ json: result }];

// OUTPUT: Return Success Result
const createResult = $input.first().json;
const algorithmResult = $('Core: Determine Next Date Algorithm').first().json;

const result = {
  success: true,
  action: 'created',
  createdDate: algorithmResult.nextDate,
  reason: algorithmResult.reason,
  backlogEntryId: createResult.id,
  timestamp: new Date().toISOString()
};

console.log(`‚úÖ Backlog Creator Success: Created entry for ${result.createdDate}`);
console.log(`üìù Reason: ${result.reason}`);
console.log(`üÜî Entry ID: ${result.backlogEntryId}`);

return [{ json: result }];

// The input is an array, so get the first item
const emailArray = $input.first().json;
const email = Array.isArray(emailArray) ? emailArray[0] : emailArray;

console.log('=== EMAIL DATA ===');
