} catch (error) {
  aiAnalysis = { error: 'Failed to parse' };
}

const result = {
  email: {
    id: emailData.id,
    threadId: emailData.threadId,
    subject: emailData.subject,
    from: emailData.from,
    to: emailData.to,
    snippet: emailData.snippet,
    processedAt: emailData.processedAt
  },
  analysis: aiAnalysis,
  processing: {
    time_ms: Math.round(aiResponse.total_duration / 1000000),
    model: aiResponse.model,
    completed_at: new Date().toISOString()
  }
};

return result;

