// Node 2: Input Validation & Environment Setup
const input = $json;
console.log('üîç Sub-workflow input received:', JSON.stringify(input, null, 2));

// Validate required field for PlannerPerson creation
if (!input.emailAddress) {
  console.error('‚ùå Missing required field: emailAddress');
  return {
    valid: false,
    error: "Missing required field: emailAddress",
    details: "Email address must be provided for PlannerPerson creation"
  };
}

// Validate email format (basic check for valid email)
if (typeof input.emailAddress !== 'string' || input.emailAddress.trim() === '') {
  console.error('‚ùå Invalid email format:', input.emailAddress);
  return {
    valid: false,
    error: "Invalid email format",
    details: "Email address must be a non-empty string"
  };
}

// Basic email validation (contains @ symbol)
if (!input.emailAddress.includes('@')) {
  console.error('‚ùå Invalid email format:', input.emailAddress);
  return {
    valid: false,
    error: "Invalid email format",
    details: "Email address must contain @ symbol"
  };
}

console.log(`‚úÖ Valid sub-workflow input for email: ${input.emailAddress}`);
return {
  valid: true,
  processedInput: {
    emailAddress: input.emailAddress.toLowerCase().trim(),
    plannerFirmId: input.plannerFirmId || null,
    backlogId: input.backlogId || ''
  }
};

// Node 5: Process Query Results
const queryResults = $input.all();
const inputData = $('N01: Input Validation & Environment Setup').first().json.processedInput;

console.log('üîç Duplicate check results:', queryResults.length, 'records found');

// Check if no results or empty result object
if (queryResults.length === 0 || !queryResults[0].json || Object.keys(queryResults[0].json).length === 0 || !queryResults[0].json.id) {
  console.log('‚úÖ No existing record found - will create new');
  return {
    recordExists: false,
    existingRecord: null,
    inputData: inputData,
    backlogId: inputData.backlogId,
    action: "create_new"
  };
}

if (queryResults.length > 1) {
  console.warn('‚ö†Ô∏è Multiple records found for email:', inputData.emailAddress);
  // Use first record but log the issue
}

const existingRecord = queryResults[0].json;
console.log('‚ÑπÔ∏è Existing record found - will return existing');
console.log('üìÑ Existing record ID:', existingRecord.id);

return {
  recordExists: true,
  existingRecord: existingRecord,
  inputData: inputData,
  backlogId: inputData.backlogId,
  action: "found_existing"
};

// Node 8: Format Database Record Response
const input = $json;
const action = $('N04: Process Query Results').first().json.action;

console.log('‚úÖ Formatting database record');
console.log('üìÑ Input data:', JSON.stringify(input, null, 2));

// Check if this is coming from the existing record path or new record path
let recordData;

if (action === 'found_existing') {
  // For existing records, the data is already formatted from N04
  const existingData = $('N04: Process Query Results').first().json.existingRecord;

  recordData = {
    id: existingData.id,
    url: existingData.url,
    property_email_address: existingData.property_email_address || '',
    property_status: existingData.property_status || 'pending',
    // Relations are already in the right format
    rel_backlog: existingData.property_rel_backlog?.[0] || null,
    rel_planner_email: existingData.property_rel_planner_email?.[0] || null,
    rel_planner_firm: existingData.property_rel_planner_firm?.[0] || null,
    rel_person: existingData.property_rel_person?.[0] || null,
    action: action,
    created_time: existingData.property_created_time || existingData.created_time,
    last_edited_time: existingData.property_last_edited_time || existingData.last_edited_time
  };
} else {
  // For new records, check if we have input at all
  if (!input || Object.keys(input).length === 0) {
    // If no input, get the data from N07 directly
    const n07Data = $('N07: Query Created Record').first().json;
    recordData = {
      id: n07Data.id,
      url: n07Data.url,
      property_email_address: n07Data.property_email_address || '',
      property_status: n07Data.property_status || 'pending',
      // Relations are already in the right format
      rel_backlog: n07Data.property_rel_backlog?.[0] || null,
      rel_planner_email: n07Data.property_rel_planner_email?.[0] || null,
      rel_planner_firm: n07Data.property_rel_planner_firm?.[0] || null,
      rel_person: n07Data.property_rel_person?.[0] || null,
      action: 'created_new',
      created_time: n07Data.property_created_time || n07Data.created_time,
      last_edited_time: n07Data.property_last_edited_time || n07Data.last_edited_time
    };
  } else {
    // If we have input, use it (data comes from N07 with properties at root)
    recordData = {
      id: input.id,
      url: input.url,
      property_email_address: input.property_email_address || '',
      property_status: input.property_status || 'pending',
      // Relations are already in the right format
      rel_backlog: input.property_rel_backlog?.[0] || null,
      rel_planner_email: input.property_rel_planner_email?.[0] || null,
      rel_planner_firm: input.property_rel_planner_firm?.[0] || null,
      rel_person: input.property_rel_person?.[0] || null,
      action: 'created_new',
      created_time: input.property_created_time || input.created_time,
      last_edited_time: input.property_last_edited_time || input.last_edited_time
    };
  }
}

// Add execution and workflow information
recordData.childExecutionId = $execution.id;
recordData.childWorkflowId = $workflow.id;
recordData.childWorkflowName = $workflow.name;

// Return in nested structure with database table name
const response = {
  PlannerPerson: recordData
};

console.log('‚úÖ Database record formatted successfully');
return response;

// Node 8: Error Output
const validationResult = $input.first().json;

console.error('‚ùå Sub-workflow error:', validationResult.error);

return {
  success: false,
  error: validationResult.error,
  details: validationResult.details,
  action: "error",
  timestamp: new Date().toISOString()
};
