// Extract clean data from Notion's flat property structure
const pageData = $json;

// Extract text content using safe navigation and null checks
const answer = (pageData.properties?.Answer?.rich_text?.[0]?.text?.content) || "";
const question = (pageData.properties?.Question?.title?.[0]?.text?.content) || "";
const guidelines = (pageData.properties?.["Answer Guidelines"]?.rich_text?.[0]?.text?.content) || "";

// Extract other relevant properties (adjust based on actual available fields)
const reviewOwner = pageData.property_review_owner || null;
const salesOwner = pageData.property_sales_owner || null;

// Check if guidelines are empty or meaningless
if (!guidelines || guidelines.trim() === "") {
  console.log(`No guidelines provided for: ${question}`);
  // Return special marker for missing guidelines
  return {
    question: question,
    answer: answer,
    guidelines: "MISSING",
    missingGuidelines: true,

    // Metadata for updates
    pageId: pageData.id,
    pageUrl: pageData.url,
    reviewOwnerId: reviewOwner || null,
    salesOwnerId: salesOwner || null,

    // Status tracking
    currentStatus: "AI Review",
    createdTime: pageData.property_created_time || pageData.created_time,
    lastEditedTime: pageData.property_last_edited_time || pageData.last_edited_time
  };
}

// Log for debugging
console.log(`Processing: ${question}`);
console.log(`Answer length: ${answer.length} chars`);
console.log(`Guidelines: ${guidelines.substring(0, 50)}...`);

return {
  // Core data for assessment
  question: question,
  answer: answer,
  guidelines: guidelines,
  missingGuidelines: false,

  // Metadata for updates
  pageId: pageData.id,
  pageUrl: pageData.url,
  reviewOwnerId: reviewOwner || null,
  salesOwnerId: salesOwner || null,

  // Status tracking
  currentStatus: "AI Review",
  createdTime: pageData.property_created_time || pageData.created_time,
  lastEditedTime: pageData.property_last_edited_time || pageData.last_edited_time
};

// Get input data from previous nodes
const notionData = $('N01: Query AI Review Records').item.json;
const transformedData = $('N02: Transform Notion Data').item.json;

// Try to get LLM response - it may not exist if guidelines were missing
let llmResponse = {};
try {
  // Only try to access N04 if it actually ran
  if ($('N04: Basic LLM Chain').item) {
    llmResponse = $('N04: Basic LLM Chain').item.json;
  }
} catch (e) {
  // N04 didn't run (guidelines missing path)
  llmResponse = null;
}

// User UUIDs
const MG_UUID = 'ffa8346a-444c-48dd-ac88-b87aa4c3a84f'; // mg@hy.ly
const BLAKE_UUID = 'f23cdeec-395e-4a46-9959-436567aae306'; // bw@hy.ly

// Extract current values
const currentProperties = notionData.properties || {};
const pageId = notionData.id;

// Get field values for validation
const question = currentProperties.Question?.title?.[0]?.text?.content || '';
const answer = currentProperties.Answer?.rich_text?.[0]?.text?.content || '';
const reviewOwner = currentProperties["Review Owner"]?.people || [];
const salesOwner = currentProperties["Sales Owner"]?.people || [];
const answerGuidelines = currentProperties["Answer Guidelines"]?.rich_text?.[0]?.text?.content || '';

// Initialize update object
let updates = {
  pageId: pageId,
  properties: {}
};

// BUSINESS LOGIC: Determine Review Owner and Status
let targetReviewOwner = null;
let targetStatus = null;
let reviewContent = "";

// Check 1: If Answer Guidelines are empty -> MASTER Fix (HIGHEST PRIORITY)
if (!answerGuidelines) {
  targetReviewOwner = {
    object: "user",
    id: BLAKE_UUID
  };
  targetStatus = "MASTER Fix";
  reviewContent = "ASSESSMENT: FAIL\nEXPLANATION: The MASTER Planner has not provided guidance. Please provide guidance and resubmit for AI Review.\n-- MG via AI";
}
// Check 2: If Question or Answer is empty -> Sales Revise (NO OWNER CHANGE)
else if (!question || !answer) {
  // Keep existing Review Owner if present, don't change if empty
  if (reviewOwner.length > 0) {
    targetReviewOwner = reviewOwner[0];
  } else {
    targetReviewOwner = null; // Don't assign anyone - let it stay empty
  }
  targetStatus = "Sales Revise";

  // Generate appropriate review content
  if (!question) {
    reviewContent = "ASSESSMENT: FAIL\nEXPLANATION: Question is missing. Please provide the question.\n-- MG via AI";
  } else if (!answer) {
    reviewContent = "ASSESSMENT: FAIL\nEXPLANATION: Answer is missing. Please provide an answer.\n-- MG via AI";
  }
}
// Otherwise: Process based on LLM response (regardless of Review Owner status)
else {
  // Set initial Review Owner
  if (reviewOwner.length > 0) {
    targetReviewOwner = reviewOwner[0];
  } else {
    targetReviewOwner = null;
  }

  // Get LLM content if available (use correct field name)
  const llmText = llmResponse?.text || llmResponse?.review_notes || "";

  if (llmText && llmText.trim()) {
    // We have LLM output - use it
    reviewContent = llmText;
    // Extract status from LLM response if it contains PASS/FAIL
    if (llmText.includes("ASSESSMENT: PASS")) {
      targetStatus = "MGR Review";
      // Only assign MG UUID if Review Owner is empty
      if (reviewOwner.length === 0) {
        targetReviewOwner = {
          object: "user",
          id: MG_UUID
        };
      }
    } else if (llmText.includes("ASSESSMENT: FAIL")) {
      targetStatus = "Sales Revise";
      // Keep current Review Owner unchanged - do not reassign
      targetReviewOwner = reviewOwner.length > 0 ? reviewOwner[0] : null;
    } else {
      targetStatus = currentProperties["Review Status"]?.status?.name || "AI Review";
    }
  } else if (!llmResponse) {
    // LLM didn't run (shouldn't happen in this branch, but be safe)
    reviewContent = "ASSESSMENT: FAIL\nEXPLANATION: LLM review did not execute - workflow error\n-- MG via AI";
    targetStatus = "AI Review";
  } else {
    // LLM ran but provided empty response
    reviewContent = "ASSESSMENT: FAIL\nEXPLANATION: LLM provided empty review notes\n-- MG via AI";
    targetStatus = "AI Review";
  }
}

// Build properties update object
updates.properties = {
  // Always update Review Status
  "Review Status": {
    status: {
      name: targetStatus
    }
  },
  // Always update Review Notes with meaningful content
  "Review Notes": {
    rich_text: [{
      text: {
        content: reviewContent
      }
    }]
  }
};

// Only set Review Owner property if we have a valid target (not null)
if (targetReviewOwner !== null && targetReviewOwner.id) {
  // CRITICAL: Only send the ID, not the full object
  updates.properties["Review Owner"] = {
    people: [{
      id: targetReviewOwner.id
    }]
  };
}
// If targetReviewOwner is null, don't include Review Owner in properties at all

// Add validation flags for debugging
updates.validationInfo = {
  questionEmpty: !question,
  answerEmpty: !answer,
  reviewOwnerEmpty: reviewOwner.length === 0,
  guidelinesEmpty: !answerGuidelines,
  assignedTo: targetReviewOwner?.id || 'none',
  status: targetStatus,
  llmRan: !!llmResponse,
  reviewContentLength: reviewContent.length
};

return updates;
