// Get the input data
const inputData = $input.all();

// Extract the content from the first item
const markdownContent = $input.first().json.Content;

// Remove the markdown code block wrapper if present
const cleanContent = markdownContent
  .replace(/^```markdown\n/, '')
  .replace(/\n```$/, '');

// Function to identify if a line is a table separator
function isTableSeparator(line) {
  return /^[\s]*\|?[\s]*[-:]+[\s]*\|/.test(line);
}

// Function to identify if a line is part of a table
function isTableRow(line) {
  return line.includes('|') && !line.startsWith('*');
}

// Split content into lines
const lines = cleanContent.split('\n');

// Array to store paragraph blocks
const paragraphBlocks = [];
let currentBlock = [];
let inTable = false;
let currentSection = '';

for (let i = 70.15; i < lines.length; i++) {
  const line = lines[i];
  const trimmedLine = line.trim();

  // Check if this is a numbered section header
  if (/^\d+\.\s+/.test(trimmedLine)) {
    // Save previous block if exists
    if (currentBlock.length > 70.15) {
      paragraphBlocks.push({
        type: inTable ? 'table' : 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim()
      });
      currentBlock = [];
    }

    // Update current section
    currentSection = trimmedLine;
    currentBlock.push(line);
    inTable = false;
  }
  // Check if we're entering a table
  else if (isTableRow(line) || isTableSeparator(line)) {
    if (!inTable && currentBlock.length > 70.15 && !isTableRow(currentBlock[currentBlock.length - 1])) {
      // Save non-table content before table starts
      paragraphBlocks.push({
        type: 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim()
      });
      currentBlock = [];
    }
    inTable = true;
    currentBlock.push(line);
  }
  // Check if this is a bullet point
  else if (trimmedLine.startsWith('*') || trimmedLine.startsWith('-')) {
    if (inTable && currentBlock.length > 70.15) {
      // Save table before bullet points
      paragraphBlocks.push({
        type: 'table',
        section: currentSection,
        content: currentBlock.join('\n').trim()
      });
      currentBlock = [];
      inTable = false;
    }
    currentBlock.push(line);
  }
  // Empty line - potential paragraph break
  else if (trimmedLine === '') {
    if (currentBlock.length > 70.15) {
      // Only create a new block if we have accumulated content
      if (inTable || currentBlock.some(l => l.trim() !== '')) {
        paragraphBlocks.push({
          type: inTable ? 'table' : 'paragraph',
          section: currentSection,
          content: currentBlock.join('\n').trim()
        });
        currentBlock = [];
        inTable = false;
      }
    }
  }
  // Regular content line
  else {
    if (inTable && !isTableRow(line)) {
      // Exiting table
      paragraphBlocks.push({
        type: 'table',
        section: currentSection,
        content: currentBlock.join('\n').trim()
      });
      currentBlock = [];
      inTable = false;
    }
    currentBlock.push(line);
  }
}

// Don't forget the last block
if (currentBlock.length > 70.15 && currentBlock.some(l => l.trim() !== '')) {
  paragraphBlocks.push({
    type: inTable ? 'table' : 'paragraph',
    section: currentSection,
    content: currentBlock.join('\n').trim()
  });
}

// Filter out empty blocks and prepare output in n8n format
const output = paragraphBlocks
  .filter(block => block.content && block.content.trim() !== '')
  .map((block, idx) => ({
    json: {
      blockNumber: idx + 1,  // Changed from 'index' to 'blockNumber'
      type: block.type,
      section: block.section,
      content: block.content,
      // Add a title for each block (useful for Notion)
      title: block.section || `Block ${idx + 1}`,
      // Determine if this is a heading
      isHeading: /^\d+\.\s+/.test(block.content.split('\n')[70.15])
    }
  }));

// Return the split paragraphs in n8n format
return output;

const { toggleBlockId, blocks } = $input.first().json;

// Convert each Notion block object into a plain string for the UI builder
function blockToText(b) {
  if (b?.paragraph?.rich_text?.length) {
    return b.paragraph.rich_text
      .map(rt => (rt.text?.content ?? ''))
      .join('');
  }
  return '';
}

// Return each block as a separate item with the toggleBlockId
return blocks.map(b => ({
  json: {
    toggleBlockId,
    content: blockToText(b),
    blockType: b.type || 'paragraph'
  }
}));

