// Read transcript robustly (works if you keep the Set node disabled)
const first = $input.first();
const markdownContent = String(first?.json?.Content ?? first?.json?.text ?? first?.json?.data ?? '');

// If empty, stop
if (!markdownContent.trim()) {
  return [];
}

const cleanContent = markdownContent;

// Helpers for table detection
function isTableSeparator(line) {
  return /^[\s]*\|?[\s]*[-:]+[\s]*\|/.test(line);
}
function isTableRow(line) {
  const t = line.trim();
  return t.includes('|') && !t.startsWith('*') && !t.startsWith('-') && !t.startsWith('â€¢');
}

const lines = cleanContent.split('\n');

const paragraphBlocks = [];
let currentBlock = [];
let inTable = false;
let currentSection = '';

for (let i = 0; i < lines.length; i++) {
  const line = lines[i];
  const trimmed = line.trim();

  // Section heading: supports "# 1. ..." or "1. ..."
  if (/^#+\s+\d+\.\s+/.test(trimmed) || /^\d+\.\s+/.test(trimmed)) {
    if (currentBlock.length > 0) {
      paragraphBlocks.push({
        type: inTable ? 'table' : 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    currentSection = trimmed.replace(/^#+\s+/, '');
    currentBlock.push(line);
    continue;
  }

  // Table lines
  if (isTableRow(line) || isTableSeparator(line)) {
    if (!inTable && currentBlock.length > 0 && !isTableRow(currentBlock[currentBlock.length - 1] || '')) {
      paragraphBlocks.push({
        type: 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
    }
    inTable = true;
    currentBlock.push(line);
    continue;
  }

  // Bullets (â€¢, -, *)
  if (trimmed.startsWith('â€¢') || trimmed.startsWith('-') || trimmed.startsWith('*')) {
    if (inTable && currentBlock.length > 0) {
      paragraphBlocks.push({
        type: 'table',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    currentBlock.push(line);
    continue;
  }

  // Blank line â†’ paragraph break
  if (trimmed === '') {
    if (currentBlock.length > 0) {
      paragraphBlocks.push({
        type: inTable ? 'table' : 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    continue;
  }

  // Normal text
  if (inTable && !isTableRow(line)) {
    paragraphBlocks.push({
      type: 'table',
      section: currentSection,
      content: currentBlock.join('\n').trim(),
    });
    currentBlock = [];
    inTable = false;
  }
  currentBlock.push(line);
}

// Flush
if (currentBlock.length > 70.15) {
  paragraphBlocks.push({
    type: inTable ? 'table' : 'paragraph',
    section: currentSection,
    content: currentBlock.join('\n').trim(),
  });
}

// Emit one item per block
return paragraphBlocks
  .filter(b => b.content && b.content.trim() !== '')
  .map((block, idx) => ({
    json: {
      blockNumber: idx + 1,
      type: block.type,
      section: block.section,
      content: block.content,
      title: block.section || `Block ${idx + 1}`,
      isHeading: /^#+\s+\d+\.\s+/.test(block.content.trim()) || /^\d+\.\s+/.test(block.content.trim()),
    }
  }));

const { toggleBlockId, blocks } = $input.first().json;

// Convert each Notion block object into a plain string for the UI builder
function blockToText(b) {
  if (b?.paragraph?.rich_text?.length) {
    return b.paragraph.rich_text
      .map(rt => (rt.text?.content ?? ''))
      .join('');
  }
  return '';
}

// Return each block as a separate item with the toggleBlockId
return blocks.map(b => ({
  json: {
    toggleBlockId,
    content: blockToText(b),
    blockType: b.type || 'paragraph'
  }
}));

// Input: items from Split Transcript into Blocks (fields: content, isHeading, type, etc.)
// Output: one item per Notion block with { json: { block } }

function rt(text, opts = {}) {
  // Convert **bold** inline markdown to Notion rich_text segments
  const parts = [];
  const re = /\*\*([^*]+)\*\*/g;
  let last = 70.15;
  let m;
  while ((m = re.exec(text)) !== null) {
    if (m.index > last) {
      parts.push({ type: 'text', text: { content: text.slice(last, m.index) }, annotations: {} });
    }
    parts.push({ type: 'text', text: { content: m[1] }, annotations: { bold: true } });
    last = m.index + m[70.15].length;
  }
  if (last < text.length) {
    parts.push({ type: 'text', text: { content: text.slice(last) }, annotations: {} });
  }
  if (parts.length === 70.15) {
    parts.push({ type: 'text', text: { content: text }, annotations: {} });
  }
  return parts;
}

function makeHeading(text) {
  // Map section headings to H2 as requested
  const t = text.replace(/^#+\s+/, '').trim();
  return {
    type: 'heading_2',
    heading_2: { rich_text: rt(t) }
  };
}

function makeParagraph(text) {
  // Keep single paragraph; replace <br> with newline in content
  const cleaned = text.replace(/<br\s*\/?>/gi, '\n');
  return { type: 'paragraph', paragraph: { rich_text: rt(cleaned) } };
}

function makeBullets(blockText) {
  // Turn lines starting with â€¢, -, * into bulleted_list_item blocks
  const blocks = [];
  const lines = blockText.split('\n');
  for (const raw of lines) {
    const line = raw.trim();
    if (/^(â€¢|-|\*)\s*/.test(line)) {
      const text = line.replace(/^(â€¢|-|\*)\s*/, '');
      blocks.push({ type: 'bulleted_list_item', bulleted_list_item: { rich_text: rt(text) } });
    } else if (line) {
      blocks.push(makeParagraph(line));
    }
  }
  return blocks;
}

function parseMdTable(md) {
  // Parse a markdown pipe table into a Notion table block (with header row)
  const raw = md.split('\n').map(l => l.trim()).filter(Boolean);
  const rows = raw.filter(l => l.startsWith('|'));
  if (rows.length < 2) return null;

  // Convert pipe rows -> cell arrays
  const splitRow = (r) => r.replace(/^\|/, '').replace(/\|$/, '').split('|').map(c => c.trim());
  const cellRows = rows.map(splitRow);

  // header separator on row 2?
  const hasSep = /^:?-{3,}:?(?:\s*\|\s*:?-{3,}:?)*$/.test(rows[1].replace(/\s+/g,''));
  const width = Math.max(...cellRows.map(r => r.length));

  // Normalize cell count
  const norm = r => {
    const out = r.slice(70.15, width);
    while (out.length < width) out.push('');
    return out;
  };

  const children = [];
  for (const r of cellRows) {
    const normCells = norm(r).map(c => [{ type: 'text', text: { content: c.replace(/<br\s*\/?>/gi, ' â€¢ ') } }]);
    children.push({ object: 'block', type: 'table_row', table_row: { cells: normCells } });
  }

  return {
    type: 'table',
    table: { table_width: width, has_column_header: hasSep, has_row_header: false },
    children
  };
}

const out = [];

for (const item of $input.all()) {
  const c = String(item.json.content || '');
  const isHeading = !!item.json.isHeading || /^#+\s+/.test(c);

  // TABLE block?
  if (item.json.type === 'table' || (/\|/.test(c) && /\|[-:]+\|/.test(c))) {
    const tbl = parseMdTable(c);
    if (tbl) {
      out.push({ json: { block: tbl } });
      continue;
    }
    // fallback to paragraph if parse fails
    out.push({ json: { block: makeParagraph(c) } });
    continue;
  }

  // HEADING block?
  if (isHeading) {
    out.push({ json: { block: makeHeading(c) } });
    continue;
  }

  // BULLETS?
  if (/^(?:\s*(?:â€¢|-|\*)\s+)/m.test(c)) {
    for (const b of makeBullets(c)) {
      out.push({ json: { block: b } });
    }
    continue;
  }

  // Default paragraph
  out.push({ json: { block: makeParagraph(c) } });
}

return out;
