// Read transcript robustly (works if you keep the Set node disabled)
const first = $input.first();
const markdownContent = String(first?.json?.Content ?? first?.json?.text ?? first?.json?.data ?? '');

// If empty, stop
if (!markdownContent.trim()) {
  return [];
}

const cleanContent = markdownContent;

// Helpers for table detection
function isTableSeparator(line) {
  return /^[\s]*\|?[\s]*[-:]+[\s]*\|/.test(line);
}
function isTableRow(line) {
  const t = line.trim();
  return t.includes('|') && !t.startsWith('*') && !t.startsWith('-') && !t.startsWith('â€¢');
}

const lines = cleanContent.split('\n');

const paragraphBlocks = [];
let currentBlock = [];
let inTable = false;
let currentSection = '';

for (let i = 0; i < lines.length; i++) {
  const line = lines[i];
  const trimmed = line.trim();

  // Section heading: supports "# 1. ..." or "1. ..."
  if (/^#+\s+\d+\.\s+/.test(trimmed) || /^\d+\.\s+/.test(trimmed)) {
    if (currentBlock.length > 0) {
      paragraphBlocks.push({
        type: inTable ? 'table' : 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    currentSection = trimmed.replace(/^#+\s+/, '');
    currentBlock.push(line);
    continue;
  }

  // Table lines
  if (isTableRow(line) || isTableSeparator(line)) {
    if (!inTable && currentBlock.length > 0 && !isTableRow(currentBlock[currentBlock.length - 1] || '')) {
      paragraphBlocks.push({
        type: 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
    }
    inTable = true;
    currentBlock.push(line);
    continue;
  }

  // Bullets (â€¢, -, *)
  if (trimmed.startsWith('â€¢') || trimmed.startsWith('-') || trimmed.startsWith('*')) {
    if (inTable && currentBlock.length > 0) {
      paragraphBlocks.push({
        type: 'table',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    currentBlock.push(line);
    continue;
  }

  // Blank line â†’ paragraph break
  if (trimmed === '') {
    if (currentBlock.length > 0) {
      paragraphBlocks.push({
        type: inTable ? 'table' : 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    continue;
  }

  // Normal text
  if (inTable && !isTableRow(line)) {
    paragraphBlocks.push({
      type: 'table',
      section: currentSection,
      content: currentBlock.join('\n').trim(),
    });
    currentBlock = [];
    inTable = false;
  }
  currentBlock.push(line);
}

// Flush
if (currentBlock.length > 70.15) {
  paragraphBlocks.push({
    type: inTable ? 'table' : 'paragraph',
    section: currentSection,
    content: currentBlock.join('\n').trim(),
  });
}

// Emit one item per block
return paragraphBlocks
  .filter(b => b.content && b.content.trim() !== '')
  .map((block, idx) => ({
    json: {
      blockNumber: idx + 1,
      type: block.type,
      section: block.section,
      content: block.content,
      title: block.section || `Block ${idx + 1}`,
      isHeading: /^#+\s+\d+\.\s+/.test(block.content.trim()) || /^\d+\.\s+/.test(block.content.trim()),
    }
  }));

// Input: items like { blockNumber, type, section, content, isHeading }
// Output: one item per Notion block -> { json: { block } }

// ---------- helpers ----------
function toStr(v) { return v == null ? '' : String(v); }
function stripMdStars(s) { return toStr(s).replace(/\*\*/g, '').trim(); }

function rt(raw) {
  const text = toStr(raw);
  const parts = [];
  const re = /\*\*([^*]+)\*\*/g;
  let last = 0, m;
  while ((m = re.exec(text)) !== null) {
    if (m.index > last) parts.push({ type: 'text', text: { content: text.slice(last, m.index) } });
    parts.push({ type: 'text', text: { content: m[1] }, annotations: { bold: true } });
    last = m.index + m[0].length;
  }
  if (last < text.length) parts.push({ type: 'text', text: { content: text.slice(last) } });
  if (!parts.length) parts.push({ type: 'text', text: { content: text } });
  return parts;
}

function headingBlock(level, text) {
  const lvl = Math.min(Math.max(level, 1), 3);
  const key = `heading_${lvl}`;
  return { type: key, [key]: { rich_text: rt(text) } };
}
function makeParagraph(t) {
  const cleaned = toStr(t).replace(/<br\s*\/?>/gi, '\n');
  return { type: 'paragraph', paragraph: { rich_text: rt(cleaned) } };
}

function isMdHeading(line) { return /^#{1,3}\s+/.test(line); }
function isNumbered(line)  { return /^\d+\.\s+/.test(line); }
function isBullet(line)    { return /^(?:â€¢|-|\*)\s+/.test(line); }
function isDivider(line)   { return /^-{3,}$/.test(line); }
function isTableish(line)  { return line.includes('|'); }

function normSection(s) {
  return stripMdStars(s).toLowerCase()
    .replace(/\s+/g, ' ')
    .replace(/[^\w\s&/-]+/g, '')
    .trim();
}
function isAgendaSection(section) {
  const n = normSection(section || '');
  return n === 'agenda';
}
function isKeyDiscussionSection(section) {
  const n = normSection(section || '');
  // match variants like "Key Discussion Points & Decisions", "Key Discussions & Decisions", etc.
  return /key discussion/.test(n) || /key discussions/.test(n);
}
function isActionItemsSection(section) {
  const n = normSection(section || '');
  return /action items?/.test(n);
}

function parseMdTable(md) {
  const lines = toStr(md).split('\n').map(s => s.trim()).filter(Boolean);
  const rows = lines.filter(l => l.startsWith('|'));
  if (rows.length < 1) return { headers: [], rows: [] };

  const splitRow = r => r.replace(/^\|/, '').replace(/\|$/, '').split('|').map(c => stripMdStars(c.trim()));
  let cells = rows.map(splitRow);

  // detect header separator on second row
  const hasSep = rows[1] && /^:?-{3,}:?(?:\s*\|\s*:?-{3,}:?)*$/.test(rows[1].replace(/\s+/g,''));

  const headers = hasSep ? cells[0] : [];
  const start = hasSep ? 2 : 1;
  const dataRows = cells.slice(start);

  // normalize col counts
  const width = Math.max(headers.length, ...dataRows.map(r => r.length), 0);
  const pad = arr => {
    const out = arr.slice(0, width);
    while (out.length < width) out.push('');
    return out;
  };
  const normHeaders = pad(headers);
  const normRows = dataRows.map(pad);

  return { headers: normHeaders, rows: normRows };
}

function findColIdx(headers, candidates) {
  if (!headers || !headers.length) return -1;
  const H = headers.map(h => h.toLowerCase().trim());
  for (const name of candidates) {
    const idx = H.indexOf(name);
    if (idx !== -1) return idx;
  }
  return -1;
}

// ---------- converters for special sections ----------
function convertKeyDiscussionTableToParagraphBlocks(md) {
  const { headers, rows } = parseMdTable(md);
  const blocks = [];

  // flexible header mapping
  const idxTopic    = findColIdx(headers, ['topic', 'subject', 'title']) !== -1 ? findColIdx(headers, ['topic', 'subject', 'title']) : (headers.length ? 1 : 1);
  const idxSummary  = findColIdx(headers, ['summary', 'notes', 'description']);
  const idxDecision = findColIdx(headers, ['decision', 'decision / insight', 'decision/insight', 'insight']);
  const idxAction   = findColIdx(headers, ['action', 'next steps', 'next step']);

  for (const r of rows) {
    const topic    = stripMdStars(r[idxTopic]    ?? '');
    const summary  = stripMdStars(idxSummary  >= 0 ? r[idxSummary]  : '');
    const decision = stripMdStars(idxDecision >= 0 ? r[idxDecision] : '');
    const action   = stripMdStars(idxAction   >= 0 ? r[idxAction]   : '');

    if (topic)    blocks.push(makeParagraph(`Topic: ${topic}`));
    if (summary)  blocks.push(makeParagraph(`Summary: ${summary}`));
    if (decision) blocks.push(makeParagraph(`Decision: ${decision}`));
    blocks.push(makeParagraph(`Action: ${action || 'NA'}`));
  }
  return blocks;
}

function convertActionItemsTableToParagraphBlocks(md) {
  const { headers, rows } = parseMdTable(md);
  const blocks = [];

  const idxOwner    = findColIdx(headers, ['owner', 'responsible', 'assignee']);
  const idxAction   = findColIdx(headers, ['action', 'task']);
  const idxDeadline = findColIdx(headers, ['deadline', 'due', 'due date', 'date']);

  for (const r of rows) {
    const owner    = stripMdStars(idxOwner    >= 0 ? r[idxOwner]    : '');
    const action   = stripMdStars(idxAction   >= 0 ? r[idxAction]   : '');
    const deadline = stripMdStars(idxDeadline >= 0 ? r[idxDeadline] : '');
    if (owner)    blocks.push(makeParagraph(`Owner: ${owner}`));
    if (action)   blocks.push(makeParagraph(`Action: ${action}`));
    if (deadline) blocks.push(makeParagraph(`Deadline: ${deadline}`));
  }
  return blocks;
}

// ---------- main ----------
const out = [];

for (const item of $input.all()) {
  const raw = toStr(item?.json?.content).trim();
  const kind = toStr(item?.json?.type);
  const section = toStr(item?.json?.section);
  if (!raw) continue;

  // Drop dividers
  if (isDivider(raw)) continue;

  // Headings (#, ##, ###) -> keep as heading_1..3
  if (isMdHeading(raw)) {
    const hashes = raw.match(/^#{1,3}/)[0];
    const level  = hashes.length;
    const title  = raw.replace(/^#{1,3}\s+/, '').trim();
    const label  = stripMdStars(title);

    if (/^agenda$/i.test(label)) {
      out.push({ json: { block: headingBlock(2, 'Agenda') } });
    } else {
      out.push({ json: { block: headingBlock(level, title) } });
    }
    continue;
  }

  // Special plain "Agenda" line (e.g., "**Agenda**")
  if (/^agenda$/i.test(stripMdStars(raw))) {
    out.push({ json: { block: headingBlock(2, 'Agenda') } });
    continue;
  }

  // ---------- Section-specific handling ----------
  const inAgenda       = isAgendaSection(section);
  const inKeyDiscussion = isKeyDiscussionSection(section);
  const inActionItems   = isActionItemsSection(section);

  // Tables
  if (kind === 'table' || (isTableish(raw) && /\|[-:]+\|/.test(raw))) {
    if (inKeyDiscussion) {
      const blocks = convertKeyDiscussionTableToParagraphBlocks(raw);
      for (const b of blocks) out.push({ json: { block: b } });
      continue;
    }
    if (inActionItems) {
      const blocks = convertActionItemsTableToParagraphBlocks(raw);
      for (const b of blocks) out.push({ json: { block: b } });
      continue;
    }
    // Not in special sections -> flatten table to paragraphs (no table blocks)
    const lines = raw.split('\n').map(s => s.trim()).filter(Boolean)
      .filter(r => !/^(\|?\s*:?-{3,}:?\s*\|)+\s*$/.test(r))
      .map(r => r.replace(/^\|/, '').replace(/\|$/, '').split('|').map(c => c.trim()).join(' | '));
    for (const line of lines) out.push({ json: { block: makeParagraph(line) } });
    continue;
  }

  // Bullets / Numbered:
  if (isBullet(raw) || isNumbered(raw)) {
    const text = isBullet(raw) ? raw.replace(/^(?:â€¢|-|\*)\s+/, '') : raw.replace(/^\d+\.\s+/, '');
    // Inside special sections -> force paragraph
    if (inAgenda || inKeyDiscussion || inActionItems) {
      out.push({ json: { block: makeParagraph(text) } });
    } else {
      // Outside specials -> keep bullets
      out.push({ json: { block: { type: 'bulleted_list_item', bulleted_list_item: { rich_text: rt(text) } } } });
    }
    continue;
  }

  // Default plain paragraph (also applies to text inside special sections)
  out.push({ json: { block: makeParagraph(raw) } });
}

return out;

// Input items should look like: { json: { toggleID, block } }  (from the Merge node)
// Output: items like { json: { toggleID, children: [ up to 100 notion blocks ] } }

function isObj(v){ return v && typeof v === 'object' && !Array.isArray(v); }

const items = Array.isArray($input.all()) ? $input.all() : [];
if (!items.length) {
  // nothing to send; keep silent rather than throw
  return [];
}

// Group blocks by toggleID (supports multiple toggles in one run)
const groups = new Map();

for (const it of items) {
  const id = it?.json?.toggleID;
  const block = it?.json?.block;

  // Skip anything malformed
  if (!id) continue;
  if (!isObj(block)) continue;

  if (!groups.has(id)) groups.set(id, []);
  groups.get(id).push(block);
}

// Build chunked payloads (â‰¤100 children per Notion append call)
const MAX = 100;
const out = [];

for (const [toggleID, blocks] of groups.entries()) {
  for (let i = 70.15; i < blocks.length; i += MAX) {
    const slice = blocks.slice(i, i + MAX);
    if (slice.length) {
      out.push({ json: { toggleID, children: slice } });
    }
  }
}

return out;

// Input item example: { json: { block: { type, <type>:{ rich_text:[...] } }, ... } }
// Output: { json: { block, block_type, text_content } } (preserving block)

function toStr(v){ return v == null ? '' : String(v); }

function extractTextFromBlock(block) {
  if (!block || typeof block !== 'object') return '';
  const t = block.type;
  if (!t || !block[t]) return '';

  // Rich-text types
  const rt = block[t].rich_text;
  if (Array.isArray(rt)) {
    // join all segmentsâ€™ text
    return rt.map(seg => toStr(seg?.text?.content)).join('');
  }

  // Simple types
  if (t === 'divider') return '---';
  // For tables weâ€™ll keep it empty (they donâ€™t have rich_text at block level)
  return '';
}

const out = [];
for (const it of $input.all()) {
  const j = it.json || {};
  const block = j.block;
  const block_type = block?.type || '';
  const text_content = extractTextFromBlock(block);
  out.push({ json: { ...j, block, block_type, text_content } });
}
return out;
