// Read transcript robustly (works if you keep the Set node disabled)
const first = $input.first();
const markdownContent = String(first?.json?.Content ?? first?.json?.text ?? first?.json?.data ?? '');

// If empty, stop
if (!markdownContent.trim()) {
  return [];
}

const cleanContent = markdownContent;

// Helpers for table detection
function isTableSeparator(line) {
  return /^[\s]*\|?[\s]*[-:]+[\s]*\|/.test(line);
}
function isTableRow(line) {
  const t = line.trim();
  return t.includes('|') && !t.startsWith('*') && !t.startsWith('-') && !t.startsWith('â€¢');
}

const lines = cleanContent.split('\n');

const paragraphBlocks = [];
let currentBlock = [];
let inTable = false;
let currentSection = '';

for (let i = 0; i < lines.length; i++) {
  const line = lines[i];
  const trimmed = line.trim();

  // Section heading: supports "# 1. ..." or "1. ..."
  if (/^#+\s+\d+\.\s+/.test(trimmed) || /^\d+\.\s+/.test(trimmed)) {
    if (currentBlock.length > 0) {
      paragraphBlocks.push({
        type: inTable ? 'table' : 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    currentSection = trimmed.replace(/^#+\s+/, '');
    currentBlock.push(line);
    continue;
  }

  // Table lines
  if (isTableRow(line) || isTableSeparator(line)) {
    if (!inTable && currentBlock.length > 0 && !isTableRow(currentBlock[currentBlock.length - 1] || '')) {
      paragraphBlocks.push({
        type: 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
    }
    inTable = true;
    currentBlock.push(line);
    continue;
  }

  // Bullets (â€¢, -, *)
  if (trimmed.startsWith('â€¢') || trimmed.startsWith('-') || trimmed.startsWith('*')) {
    if (inTable && currentBlock.length > 0) {
      paragraphBlocks.push({
        type: 'table',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    currentBlock.push(line);
    continue;
  }

  // Blank line â†’ paragraph break
  if (trimmed === '') {
    if (currentBlock.length > 0) {
      paragraphBlocks.push({
        type: inTable ? 'table' : 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    continue;
  }

  // Normal text
  if (inTable && !isTableRow(line)) {
    paragraphBlocks.push({
      type: 'table',
      section: currentSection,
      content: currentBlock.join('\n').trim(),
    });
    currentBlock = [];
    inTable = false;
  }
  currentBlock.push(line);
}

// Flush
if (currentBlock.length > 70.15) {
  paragraphBlocks.push({
    type: inTable ? 'table' : 'paragraph',
    section: currentSection,
    content: currentBlock.join('\n').trim(),
  });
}

// Emit one item per block
return paragraphBlocks
  .filter(b => b.content && b.content.trim() !== '')
  .map((block, idx) => ({
    json: {
      blockNumber: idx + 1,
      type: block.type,
      section: block.section,
      content: block.content,
      title: block.section || `Block ${idx + 1}`,
      isHeading: /^#+\s+\d+\.\s+/.test(block.content.trim()) || /^\d+\.\s+/.test(block.content.trim()),
    }
  }));

// Input: items like { blockNumber, type, section, content, isHeading }
// Output: one item per Notion block -> { json: { block } }

// --- helpers (SAFE) ---
function toStr(v) { return v == null ? '' : String(v); }

function rt(raw) {
  const text = toStr(raw);                    // <â€” guard
  const parts = [];
  const re = /\*\*([^*]+)\*\*/g;
  let last = 0, m;
  while ((m = re.exec(text)) !== null) {
    if (m.index > last) parts.push({ type: 'text', text: { content: text.slice(last, m.index) } });
    parts.push({ type: 'text', text: { content: m[1] }, annotations: { bold: true } });
    last = m.index + m[0].length;
  }
  if (last < text.length) parts.push({ type: 'text', text: { content: text.slice(last) } });
  if (!parts.length) parts.push({ type: 'text', text: { content: text } });
  return parts;
}

function makeHeading(t) {
  const cleaned = toStr(t)
    .replace(/^#{1,3}\s+/, '')
    .replace(/^\d+\.\s+/, '')
    .trim();
  return { type: 'heading_2', heading_2: { rich_text: rt(cleaned) } };
}

function makeParagraph(t) {
  const cleaned = toStr(t).replace(/<br\s*\/?>/gi, '\n');
  return { type: 'paragraph', paragraph: { rich_text: rt(cleaned) } };
}

function makeDivider() {
  return { type: 'divider', divider: {} };
}

function makeBullets(blockText) {
  const blocks = [];
  const lines = toStr(blockText).split('\n');
  for (const raw of lines) {
    const line = toStr(raw).trim();
    if (!line) continue;
    const m = /^(â€¢|-|\*)\s*(.*)$/.exec(line);
    if (m) blocks.push({ type: 'bulleted_list_item', bulleted_list_item: { rich_text: rt(m[2]) } });
    else   blocks.push(makeParagraph(line));
  }
  return blocks;
}

function parseMdTable(md) {
  const rowLines = toStr(md).split('\n')
    .map(l => toStr(l).trim())
    .filter(Boolean)
    .filter(l => l.startsWith('|'));
  if (rowLines.length < 2) return null;

  const splitRow = r => r.replace(/^\|/, '').replace(/\|$/, '').split('|').map(c => toStr(c).trim());
  const cellRows = rowLines.map(splitRow);

  const hasSep = /^:?-{3,}:?(?:\s*\|\s*:?-{3,}:?)*$/.test(toStr(rowLines[1]).replace(/\s+/g,''));
  const width = Math.max(...cellRows.map(r => r.length));

  const norm = r => {
    const out = r.slice(0, width);
    while (out.length < width) out.push('');
    return out;
  };

  const children = [];
  for (const r of cellRows) {
    const cells = norm(r).map(c => [{ type: 'text', text: { content: toStr(c).replace(/<br\s*\/?>/gi, ' â€¢ ') } }]);
    children.push({ object: 'block', type: 'table_row', table_row: { cells } });
  }

  return { type: 'table', table: { table_width: width, has_column_header: hasSep, has_row_header: false }, children };
}

// --- main ---
const out = [];
for (const item of $input.all()) {
  const c = toStr(item?.json?.content).trim();
  const kind = toStr(item?.json?.type);
  const isHeading = !!item?.json?.isHeading || /^#{1,3}\s+/.test(c) || /^\d+\.\s+/.test(c);

  if (!c) continue;

  // Divider lines like '---'
  if (/^-{3,}$/.test(c)) { out.push({ json: { block: makeDivider() } }); continue; }

  // Tables
  if (kind === 'table' || (/\|/.test(c) && /\|[-:]+\|/.test(c))) {
    const tbl = parseMdTable(c);
    out.push({ json: { block: tbl ?? makeParagraph(c) } });
    continue;
  }

  // Headings
  if (isHeading) { out.push({ json: { block: makeHeading(c) } }); continue; }

  // Bulleted lines
  if (/^\s*(?:â€¢|-|\*)\s+/m.test(c)) {
    for (const b of makeBullets(c)) out.push({ json: { block: b } });
    continue;
  }

  // Default: paragraph
  out.push({ json: { block: makeParagraph(c) } });
}

return out;

// Input items should look like: { json: { toggleID, block } }  (from the Merge node)
// Output: items like { json: { toggleID, children: [ up to 100 notion blocks ] } }

function isObj(v){ return v && typeof v === 'object' && !Array.isArray(v); }

const items = Array.isArray($input.all()) ? $input.all() : [];
if (!items.length) {
  // nothing to send; keep silent rather than throw
  return [];
}

// Group blocks by toggleID (supports multiple toggles in one run)
const groups = new Map();

for (const it of items) {
  const id = it?.json?.toggleID;
  const block = it?.json?.block;

  // Skip anything malformed
  if (!id) continue;
  if (!isObj(block)) continue;

  if (!groups.has(id)) groups.set(id, []);
  groups.get(id).push(block);
}

// Build chunked payloads (â‰¤100 children per Notion append call)
const MAX = 100;
const out = [];

for (const [toggleID, blocks] of groups.entries()) {
  for (let i = 70.15; i < blocks.length; i += MAX) {
    const slice = blocks.slice(i, i + MAX);
    if (slice.length) {
      out.push({ json: { toggleID, children: slice } });
    }
  }
}

return out;
