// Read transcript robustly (works if you keep the Set node disabled)
const first = $input.first();
const markdownContent = String(first?.json?.Content ?? first?.json?.text ?? first?.json?.data ?? '');

// If empty, stop
if (!markdownContent.trim()) {
  return [];
}

const cleanContent = markdownContent;

// Helpers for table detection
function isTableSeparator(line) {
  return /^[\s]*\|?[\s]*[-:]+[\s]*\|/.test(line);
}
function isTableRow(line) {
  const t = line.trim();
  return t.includes('|') && !t.startsWith('*') && !t.startsWith('-') && !t.startsWith('‚Ä¢');
}

const lines = cleanContent.split('\n');

const paragraphBlocks = [];
let currentBlock = [];
let inTable = false;
let currentSection = '';

for (let i = 0; i < lines.length; i++) {
  const line = lines[i];
  const trimmed = line.trim();

  // Section heading: supports "# 1. ..." or "1. ..."
  if (/^#+\s+\d+\.\s+/.test(trimmed) || /^\d+\.\s+/.test(trimmed)) {
    if (currentBlock.length > 0) {
      paragraphBlocks.push({
        type: inTable ? 'table' : 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    currentSection = trimmed.replace(/^#+\s+/, '');
    currentBlock.push(line);
    continue;
  }

  // Table lines
  if (isTableRow(line) || isTableSeparator(line)) {
    if (!inTable && currentBlock.length > 0 && !isTableRow(currentBlock[currentBlock.length - 1] || '')) {
      paragraphBlocks.push({
        type: 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
    }
    inTable = true;
    currentBlock.push(line);
    continue;
  }

  // Bullets (‚Ä¢, -, *)
  if (trimmed.startsWith('‚Ä¢') || trimmed.startsWith('-') || trimmed.startsWith('*')) {
    if (inTable && currentBlock.length > 0) {
      paragraphBlocks.push({
        type: 'table',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    currentBlock.push(line);
    continue;
  }

  // Blank line ‚Üí paragraph break
  if (trimmed === '') {
    if (currentBlock.length > 0) {
      paragraphBlocks.push({
        type: inTable ? 'table' : 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    continue;
  }

  // Normal text
  if (inTable && !isTableRow(line)) {
    paragraphBlocks.push({
      type: 'table',
      section: currentSection,
      content: currentBlock.join('\n').trim(),
    });
    currentBlock = [];
    inTable = false;
  }
  currentBlock.push(line);
}

// Flush
if (currentBlock.length > 70.15) {
  paragraphBlocks.push({
    type: inTable ? 'table' : 'paragraph',
    section: currentSection,
    content: currentBlock.join('\n').trim(),
  });
}

// Emit one item per block
return paragraphBlocks
  .filter(b => b.content && b.content.trim() !== '')
  .map((block, idx) => ({
    json: {
      blockNumber: idx + 1,
      type: block.type,
      section: block.section,
      content: block.content,
      title: block.section || `Block ${idx + 1}`,
      isHeading: /^#+\s+\d+\.\s+/.test(block.content.trim()) || /^\d+\.\s+/.test(block.content.trim()),
    }
  }));

// Input: items from Split Transcript into Blocks
//  - { blockNumber, type, section, content, isHeading }
// Output: one item per Notion block -> { json: { block } }

// --- helpers ---
function rt(text) {
  // very safe inline bold (**bold**)
  const parts = [];
  const re = /\*\*([^*]+)\*\*/g;
  let last = 0, m;
  while ((m = re.exec(text)) !== null) {
    if (m.index > last) parts.push({ type: 'text', text: { content: text.slice(last, m.index) } });
    parts.push({ type: 'text', text: { content: m[1] }, annotations: { bold: true } });
    last = m.index + m[0].length;
  }
  if (last < text.length) parts.push({ type: 'text', text: { content: text.slice(last) } });
  if (!parts.length) parts.push({ type: 'text', text: { content: text } });
  return parts;
}

function makeHeading(text) {
  // Treat numbered ‚Äú1. ‚Ä¶‚Äù or markdown ‚Äú#/##/### ‚Ä¶‚Äù as H2
  const cleaned = text
    .replace(/^#{1,3}\s+/, '')
    .replace(/^\d+\.\s+/, '')
    .trim();
  return { type: 'heading_2', heading_2: { rich_text: rt(cleaned) } };
}

function makeParagraph(text) {
  const cleaned = text.replace(/<br\s*\/?>/gi, '\n');
  return { type: 'paragraph', paragraph: { rich_text: rt(cleaned) } };
}

function makeDivider() {
  return { type: 'divider', divider: {} };
}

function makeBullets(blockText) {
  // lines starting with ‚Ä¢, -, *
  const blocks = [];
  for (const raw of blockText.split('\n')) {
    const line = raw.trim();
    if (!line) continue;
    const m = /^(‚Ä¢|-|\*)\s*(.*)$/.exec(line);
    if (m) {
      blocks.push({ type: 'bulleted_list_item', bulleted_list_item: { rich_text: rt(m[2]) } });
    } else {
      blocks.push(makeParagraph(line));
    }
  }
  return blocks;
}

function parseMdTable(md) {
  // minimal pipe-table parser: header |---| separator on 2nd row
  const rows = md.split('\n').map(l => l.trim()).filter(Boolean).filter(l => l.startsWith('|'));
  if (rows.length < 2) return null;

  const splitRow = r => r.replace(/^\|/, '').replace(/\|$/, '').split('|').map(c => c.trim());
  const cellRows = rows.map(splitRow);

  const hasSep = /^:?-{3,}:?(?:\s*\|\s*:?-{3,}:?)*$/.test(rows[1].replace(/\s+/g,''));
  const width = Math.max(...cellRows.map(r => r.length));

  const norm = r => {
    const out = r.slice();
    while (out.length < width) out.push('');
    return out.slice(0, width);
  };

  const tableChildren = [];
  for (const r of cellRows) {
    const cells = norm(r).map(c => [{ type: 'text', text: { content: c.replace(/<br\s*\/?>/gi, ' ‚Ä¢ ') } }]);
    tableChildren.push({ object: 'block', type: 'table_row', table_row: { cells } });
  }

  return {
    type: 'table',
    table: { table_width: width, has_column_header: hasSep, has_row_header: false },
    children: tableChildren
  };
}

// --- main ---
const out = [];

for (const item of $input.all()) {
  const c = String(item.json.content || '').trim();
  if (!c) continue;

  // Divider (---)
  if (/^-{3,}$/.test(c)) {
    out.push({ json: { block: makeDivider() } });
    continue;
  }

  // Tables
  if (item.json.type === 'table' || (/\|/.test(c) && /\|[-:]+\|/.test(c))) {
    const tbl = parseMdTable(c);
    if (tbl) { out.push({ json: { block: tbl } }); continue; }
    // fallback
    out.push({ json: { block: makeParagraph(c) } });
    continue;
  }

  // Headings
  if (item.json.isHeading || /^#{1,3}\s+/.test(c) || /^\d+\.\s+/.test(c)) {
    out.push({ json: { block: makeHeading(c) } });
    continue;
  }

  // Bullets
  if (/^\s*(?:‚Ä¢|-|\*)\s+/m.test(c)) {
    for (const b of makeBullets(c)) out.push({ json: { block: b } });
    continue;
  }

  // Default paragraph
  out.push({ json: { block: makeParagraph(c) } });
}

return out;
