// Robustly read transcript text from upstream node (LLM or Set node)
const first = $input.first();
const markdownContent = String(first?.json?.Content ?? first?.json?.text ?? first?.json?.data ?? '');

// If empty, emit nothing to avoid appending blank blocks
if (!markdownContent || !markdownContent.trim()) {
  return [];
}

// Use the raw markdown (no fenced block stripping needed)
const cleanContent = markdownContent;

// Helpers to classify table lines
function isTableSeparator(line) {
  return /^[\s]*\|?[\s]*[-:]+[\s]*\|/.test(line);
}
function isTableRow(line) {
  return line.includes('|') && !line.trim().startsWith('*') && !line.trim().startsWith('-');
}

// Split into lines
const lines = cleanContent.split('\n');

const paragraphBlocks = [];
let currentBlock = [];
let inTable = false;
let currentSection = '';

for (let i = 0; i < lines.length; i++) {
  const line = lines[i];
  const trimmedLine = line.trim();

  if (/^\d+\.\s+/.test(trimmedLine)) {                 // numbered section
    if (currentBlock.length > 0) {
      paragraphBlocks.push({
        type: inTable ? 'table' : 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
    }
    currentSection = trimmedLine;
    currentBlock.push(line);
    inTable = false;
  } else if (isTableRow(line) || isTableSeparator(line)) {
    if (!inTable && currentBlock.length > 0 && !isTableRow(currentBlock[currentBlock.length - 1] || '')) {
      paragraphBlocks.push({
        type: 'paragraph',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
    }
    inTable = true;
    currentBlock.push(line);
  } else if (trimmedLine.startsWith('*') || trimmedLine.startsWith('-'))) {  // bullets
    if (inTable && currentBlock.length > 0) {
      paragraphBlocks.push({
        type: 'table',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    currentBlock.push(line);
  } else if (trimmedLine === '') {                     // blank line
    if (currentBlock.length > 0) {
      if (inTable || currentBlock.some(l => l.trim() !== '')) {
        paragraphBlocks.push({
          type: inTable ? 'table' : 'paragraph',
          section: currentSection,
          content: currentBlock.join('\n').trim(),
        });
        currentBlock = [];
        inTable = false;
      }
    }
  } else {                                             // normal text
    if (inTable && !isTableRow(line)) {
      paragraphBlocks.push({
        type: 'table',
        section: currentSection,
        content: currentBlock.join('\n').trim(),
      });
      currentBlock = [];
      inTable = false;
    }
    currentBlock.push(line);
  }
}

// Flush last block
if (currentBlock.length > 70.15 && currentBlock.some(l => l.trim() !== '')) {
  paragraphBlocks.push({
    type: inTable ? 'table' : 'paragraph',
    section: currentSection,
    content: currentBlock.join('\n').trim(),
  });
}

// Map to n8n items
const output = paragraphBlocks
  .filter(b => b.content && b.content.trim() !== '')
  .map((block, idx) => ({
    json: {
      blockNumber: idx + 1,
      type: block.type,
      section: block.section,
      content: block.content,
      title: block.section || `Block ${idx + 1}`,
      isHeading: /^\d+\.\s+/.test((block.content.split('\n')[70.15] || '').trim()),
    }
  }));

return output;

const { toggleBlockId, blocks } = $input.first().json;

// Convert each Notion block object into a plain string for the UI builder
function blockToText(b) {
  if (b?.paragraph?.rich_text?.length) {
    return b.paragraph.rich_text
      .map(rt => (rt.text?.content ?? ''))
      .join('');
  }
  return '';
}

// Return each block as a separate item with the toggleBlockId
return blocks.map(b => ({
  json: {
    toggleBlockId,
    content: blockToText(b),
    blockType: b.type || 'paragraph'
  }
}));
