const markdown = $input.first().json.text;
const blocks = [];

// Parse markdown formatting to rich text
function parseToRichText(text) {
  const richTextArray = [];
  const parts = text.split(/(\*\*.*?\*\*)/g);

  parts.forEach(part => {
    if (part.startsWith('**') && part.endsWith('**') && part.length > 4) {
      // Bold text
      richTextArray.push({
        type: 'text',
        text: { content: part.slice(2, -2) },
        annotations: { bold: true }
      });
    } else if (part.trim()) {
      // Regular text
      richTextArray.push({
        type: 'text',
        text: { content: part }
      });
    }
  });

  return richTextArray;
}

// Split by lines and process
const lines = markdown.split('\n');

for (const line of lines) {
  const trimmed = line.trim();

  if (!trimmed || trimmed === '---') continue; // Skip empty lines and separators

  let blockType;
  let content = trimmed;

  if (trimmed.startsWith('# ')) {
    blockType = 'heading_1';
    content = trimmed.substring(2).trim();
  } else if (trimmed.startsWith('## ')) {
    blockType = 'heading_2';
    content = trimmed.substring(3).trim();
  } else if (trimmed.startsWith('### ')) {
    blockType = 'heading_3';
    content = trimmed.substring(4).trim();
  } else if (trimmed.startsWith('- ')) {
    blockType = 'bulleted_list_item';
    content = trimmed.substring(2).trim();
  } else if (trimmed.match(/^\d+\. /)) {
    blockType = 'numbered_list_item';
    content = trimmed.replace(/^\d+\. /, '').trim();
  } else if (trimmed.startsWith('> ')) {
    // Blockquotes become styled paragraphs
    blockType = 'paragraph';
    content = trimmed.substring(2).trim();
  } else if (trimmed.startsWith('|')) {
    // Table rows become paragraphs
    blockType = 'paragraph';
    content = trimmed;
  } else {
    // Regular text becomes paragraph
    blockType = 'paragraph';
    content = trimmed;
  }

  // Generate blocks with rich text
  blocks.push({ 
    type: blockType,
    [blockType]: {
      rich_text: parseToRichText(content)
    }
  });
}

// Return the array as a field so Split Out can process it
return [{ blocks: blocks }];
